// Calculate distance between two points using Haversine formula
function calculateDistance(
  lat1: number,
  lon1: number,
  lat2: number,
  lon2: number
): number {
  const R = 6371e3; // Earth's radius in meters
  const φ1 = (lat1 * Math.PI) / 180;
  const φ2 = (lat2 * Math.PI) / 180;
  const Δφ = ((lat2 - lat1) * Math.PI) / 180;
  const Δλ = ((lon2 - lon1) * Math.PI) / 180;

  const a =
    Math.sin(Δφ / 2) * Math.sin(Δφ / 2) +
    Math.cos(φ1) * Math.cos(φ2) * Math.sin(Δλ / 2) * Math.sin(Δλ / 2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));

  return R * c; // Distance in meters
}

export async function findNearbyTrendySpots(lat: number, lng: number, radius: number, limit?: number) {
  try {


    // Trendy spots query focusing on chai lattes, matcha, poki bowls, acai spots, reformer pilates, cute workout classes
    const query = `
      [out:json][timeout:15];
      (
        node["amenity"~"^(cafe|restaurant|juice_bar|bar)$"](around:${radius},${lat},${lng});
        way["amenity"~"^(cafe|restaurant|juice_bar|bar)$"](around:${radius},${lat},${lng});
        node["shop"~"^(coffee|tea|bakery|health_food|sports|organic)$"](around:${radius},${lat},${lng});
        way["shop"~"^(coffee|tea|bakery|health_food|sports|organic)$"](around:${radius},${lat},${lng});
        node["leisure"~"^(fitness_centre|sports_centre)$"](around:${radius},${lat},${lng});
        way["leisure"~"^(fitness_centre|sports_centre)$"](around:${radius},${lat},${lng});
        node["sport"~"^(fitness|yoga|pilates|dance|climbing)$"](around:${radius},${lat},${lng});
        way["sport"~"^(fitness|yoga|pilates|dance|climbing)$"](around:${radius},${lat},${lng});
        node["cuisine"~"^(japanese|korean|thai|vietnamese|healthy|vegan|raw_food)$"](around:${radius},${lat},${lng});
        way["cuisine"~"^(japanese|korean|thai|vietnamese|healthy|vegan|raw_food)$"](around:${radius},${lat},${lng});
      );
      out geom;
    `;



    const response = await fetch('https://overpass-api.de/api/interpreter', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: `data=${encodeURIComponent(query)}`
    });

    if (!response.ok) {

      return [];
    }

    const data = await response.json();


    if (!data.elements || data.elements.length === 0) {

      return [];
    }



    // Convert OSM data to our spot format
    const spots = data.elements
      .filter((element: any) => element.lat && element.lon && element.tags && element.tags.name)
      .map((element: any) => convertOSMToSpot(element, lat, lng))
      .filter((spot: any) => spot && isTrendyPlace(spot));


    const uniqueResults = removeDuplicates(spots);


    // Sort by distance for nearby spots and limit early for performance
    const finalResults = uniqueResults
      .sort((a, b) => (a.distance || 0) - (b.distance || 0))
      .slice(0, 20); // Reduced from 25 to 20 for faster response


    return finalResults;

  } catch (error) {


    // Failsafe: Return a minimal set of known London spots if OSM fails

    return [
      {
        id: 'emergency-starbucks',
        name: 'Starbucks',
        description: 'Coffee shop',
        latitude: 51.5070,
        longitude: -0.2698,
        rating: 4,
        imageUrl: '/placeholder-icon.svg',
        category: 'café',
        trending: false,
        huntCount: 25,
        distance: Math.round(calculateDistance(lat, lng, 51.5070, -0.2698)),
        amenity: 'cafe',
        priceRange: '$$',
        dietaryOptions: [],
        ambiance: ['trendy'],
        amenities: []
      }
    ];
  }
}

function convertOSMToSpot(element: any, userLat: number, userLng: number) {
  const tags = element.tags || {};
  const placeLat = element.lat || (element.center ? element.center.lat : 0);
  const placeLng = element.lon || (element.center ? element.center.lon : 0);

  if (!placeLat || !placeLng) return null;

  const distance = calculateDistance(userLat, userLng, placeLat, placeLng);

  // Generate a consistent but varied rating based on name and type (whole numbers only)
  const nameHash = (tags.name || '').split('').reduce((hash: number, char: string) => hash + char.charCodeAt(0), 0);
  const rating = Math.floor(3.5 + ((nameHash % 20) / 40)); // Range: 3-4, whole numbers only

  return {
    id: element.id || Math.random().toString(),
    name: tags.name || 'Local Spot',
    description: getOSMDescription(tags),
    latitude: placeLat,
    longitude: placeLng,
    rating: Math.max(4, Math.min(5, rating)), // Ensure 4-5 range, whole numbers
    imageUrl: getAestheticImageUrl(tags),
    category: getOSMCategory(tags),
    trending: rating >= 3.8,
    huntCount: Math.floor(Math.random() * 30) + 5,
    distance: Math.round(distance),
    amenity: tags.amenity,
    cuisine: tags.cuisine,
    leisure: tags.leisure,
    sport: tags.sport,
    shop: tags.shop,
    website: tags.website,
    phone: tags.phone,
    // Add filter fields for OpenStreetMap spots
    priceRange: getOSMPriceRange(tags),
    dietaryOptions: getOSMDietaryOptions(tags),
    ambiance: getOSMAmbiance(tags),
    amenities: getOSMAmenities(tags)
  };
}

