import { formatDistanceToNow } from "date-fns";
import type { UserBadge, Badge } from "@shared/schema";

interface BadgeCardProps {
  userBadge: UserBadge & { badge: Badge };
}

export default function BadgeCard({ userBadge }: BadgeCardProps) {
