import { Button } from "@/components/ui/button";
import { Home, Map, Users, Trophy, User, Sparkles } from "lucide-react";
import { Link, useLocation } from "wouter";

export default function BottomNavigation() {
  const [location] = useLocation();

  const navItems = [
    { path: "/home", icon: Home, label: "Hunt", isActive: location === "/" || location === "/home" },
    { path: "/map", icon: Map, label: "Map", isActive: location === "/map" },
    { path: "/discover", icon: Sparkles, label: "Discover", isActive: location === "/discover" },
    // { path: "/social", icon: Users, label: "Social", isActive: location === "/social" }, // Temporarily disabled for App Store submission
    { path: "/profile", icon: User, label: "Profile", isActive: location === "/profile" },
  ];

  return (
    <div className="fixed bottom-0 left-1/2 transform -translate-x-1/2 w-full max-w-md">
      {/* Glass morphism background with enhanced blur */}
      <div className="bg-white/90 backdrop-blur-xl border-t border-pink-100 px-4 py-3 rounded-t-3xl shadow-2xl">
        <div className="flex justify-around items-center relative">
          {/* Background glow indicator */}
          <div 
            className="absolute h-12 w-16 bg-gradient-to-r from-pink-200 to-purple-200 rounded-2xl opacity-30 transition-all duration-300 ease-out"
            style={{
              transform: `translateX(${navItems.findIndex(item => item.isActive) * 72 - 144}px)`,
            }}
          />

          {navItems.map((item, index) => {
            const Icon = item.icon;

            return (
              <Link key={item.path} href={item.path} asChild>
                <Button 
                  variant="ghost" 
                  className={`flex flex-col items-center space-y-1 p-3 relative z-10 transition-all duration-200 min-h-[56px] min-w-[56px] ${
                    item.isActive 
                      ? 'text-pink-500 transform scale-110' 
                      : 'text-gray-400 hover:text-gray-600'
                  }`}
                  style={{
                    WebkitTapHighlightColor: 'rgba(236, 72, 153, 0.3)',
                    touchAction: 'manipulation',
                    WebkitUserSelect: 'none'
                  }}
                >
                  <div className={`p-2 rounded-xl transition-all duration-200 ${
                    item.isActive 
                      ? 'bg-gradient-to-r from-pink-100 to-purple-100 shadow-lg' 
                      : 'hover:bg-gray-50'
                  }`}>
                    <Icon className={`w-5 h-5 ${item.isActive ? 'animate-pulse-soft' : ''}`} />
                  </div>
                  <span className="text-xs font-medium">{item.label}</span>

                  {/* Active indicator dot */}
                  {item.isActive && (
                    <div className="absolute -top-1 w-1 h-1 bg-pink-500 rounded-full animate-pulse" />
                  )}
                </Button>
              </Link>
            );
          })}
        </div>
      </div>
    </div>
  );
}

client/src/components/celebration-animat.content:
import { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface CelebrationAnimationProps {
  show: boolean;
  onComplete: () => void;
  pointsEarned: number;
  newBadges?: any[];
  spotName: string;
}

export function CelebrationAnimation({ 
  show, 
  onComplete, 
  pointsEarned, 
  newBadges = [], 
  spotName 
}: CelebrationAnimationProps) {
  const [showFireworks, setShowFireworks] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showText, setShowText] = useState(false);

  useEffect(() => {
    if (show) {
      // Stagger the animations for maximum impact
      setShowFireworks(true);
      setShowConfetti(true);

      setTimeout(() => setShowText(true), 200);

      // Auto complete after animation
      setTimeout(() => {
        setShowFireworks(false);
        setShowConfetti(false);
        setShowText(false);
        onComplete();
      }, 4000);
    }
  }, [show, onComplete]);

  if (!show) return null;

  return (
    <AnimatePresence>
      <motion.div 
        className="fixed inset-0 z-50 flex items-center justify-center bg-black/20 backdrop-blur-sm"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
      >
        {/* Fireworks Effects */}
        {showFireworks && (
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            {[...Array(8)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute w-2 h-2 bg-gradient-to-r from-pink-400 to-purple-500 rounded-full"
                initial={{ 
                  x: typeof window !== 'undefined' ? window.innerWidth / 2 : 400, 
                  y: typeof window !== 'undefined' ? window.innerHeight / 2 : 300,
                  scale: 0 
                }}
                animate={{ 
                  x: (typeof window !== 'undefined' ? window.innerWidth / 2 : 400) + (Math.cos(i * 45 * Math.PI / 180) * 200),
                  y: (typeof window !== 'undefined' ? window.innerHeight / 2 : 300) + (Math.sin(i * 45 * Math.PI / 180) * 200),
                  scale: [0, 1, 0],
                  opacity: [0, 1, 0]
                }}
                transition={{ 
                  duration: 1.5, 
                  delay: i * 0.1,
                  repeat: 2,
                  repeatDelay: 0.5
                }}
              />
            ))}

            {/* Additional sparkles */}
            {[...Array(12)].map((_, i) => (
              <motion.div
                key={`sparkle-${i}`}
                className="absolute w-1 h-1 bg-yellow-300 rounded-full"
                initial={{ 
                  x: Math.random() * (typeof window !== 'undefined' ? window.innerWidth : 800), 
                  y: Math.random() * (typeof window !== 'undefined' ? window.innerHeight : 600),
                  scale: 0 
                }}
                animate={{ 
                  scale: [0, 1.5, 0],
                  opacity: [0, 1, 0],
                  rotate: 360
                }}
                transition={{ 
                  duration: 2, 
                  delay: Math.random() * 2,
                  repeat: 1
                }}
              />
            ))}
          </div>
        )}

        {/* Confetti Effects */}
        {showConfetti && (
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            {[...Array(25)].map((_, i) => (
              <motion.div
                key={`confetti-${i}`}
                className={`absolute w-3 h-3 ${
                  i % 4 === 0 ? 'bg-pink-400' :
                  i % 4 === 1 ? 'bg-purple-400' :
                  i % 4 === 2 ? 'bg-yellow-400' : 'bg-blue-400'
                } rounded-sm`}
                initial={{ 
                  x: Math.random() * (typeof window !== 'undefined' ? window.innerWidth : 800), 
                  y: -50,
                  rotate: 0 
                }}
                animate={{ 
                  y: (typeof window !== 'undefined' ? window.innerHeight : 600) + 50,
                  rotate: 360 * 3,
                  x: Math.random() * (typeof window !== 'undefined' ? window.innerWidth : 800)
                }}
                transition={{ 
                  duration: 3 + Math.random() * 2, 
                  delay: Math.random() * 1,
                  ease: "easeOut"
                }}
              />
            ))}
          </div>
        )}

        {/* Main Celebration Content */}
        <AnimatePresence>
          {showText && (
            <motion.div 
              className="bg-white/95 backdrop-blur-md rounded-3xl p-8 shadow-2xl max-w-sm mx-4 text-center"
              initial={{ scale: 0, rotate: -10 }}
              animate={{ scale: 1, rotate: 0 }}
              exit={{ scale: 0, rotate: 10 }}
              transition={{ 
                type: "spring", 
                stiffness: 200, 
                damping: 20 
              }}
            >
              {/* Success Icon */}
              <motion.div
                className="w-20 h-20 bg-gradient-to-r from-pink-500 to-purple-500 rounded-full mx-auto mb-4 flex items-center justify-center text-3xl"
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.2, type: "spring", stiffness: 300 }}
              >
                ✨
              </motion.div>

              {/* Title */}
              <motion.h2 
                className="text-2xl font-bold bg-gradient-to-r from-pink-600 to-purple-600 bg-clip-text text-transparent mb-2"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
              >
                Checked In!
              </motion.h2>

              {/* Spot Name */}
              <motion.p 
                className="text-gray-700 mb-4 font-medium"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.4 }}
              >
                {spotName}
              </motion.p>

              {/* Points */}
              <motion.div 
                className="bg-gradient-to-r from-yellow-100 to-yellow-200 rounded-2xl p-4 mb-4"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ delay: 0.5 }}
              >
                <div className="text-3xl font-bold text-yellow-700">
                  +{pointsEarned}
                </div>
                <div className="text-yellow-600 text-sm">Points Earned!</div>
              </motion.div>

              {/* New Badges */}
              {newBadges && newBadges.length > 0 && (
                <motion.div 
                  className="space-y-3"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.6 }}
                >
                  <h3 className="text-lg font-semibold text-gray-800">New Badge{newBadges.length > 1 ? 's' : ''}!</h3>
                  {newBadges.map((badge, index) => (
                    <motion.div
                      key={badge.id}
                      className="bg-gradient-to-r from-purple-100 to-pink-100 rounded-xl p-3 flex items-center space-x-3"
                      initial={{ opacity: 0, x: -20 }}
                      animate={{ opacity: 1, x: 0 }}
                      transition={{ delay: 0.7 + index * 0.1 }}
                    >
                      <div className="text-2xl">{badge.icon}</div>
                      <div>
                        <div className="font-semibold text-purple-800">{badge.name}</div>
                        <div className="text-sm text-purple-600">{badge.description}</div>
                      </div>
                    </motion.div>
                  ))}
                </motion.div>
              )}

              {/* Encouraging Messages */}
              <motion.div 
                className="mt-6 text-sm text-gray-600"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.8 }}
              >
                Keep exploring! ✨
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </AnimatePresence>
  );
}

client/src/components/daily-challenge.ts.content:
import { useQuery } from "@tanstack/react-query";
import { Progress } from "@/components/ui/progress";
import type { DailyChallenge, UserChallengeProgress } from "@shared/schema";

export default function DailyChallenge() {
  const { data: challenges } = useQuery<DailyChallenge[]>({
    queryKey: ["/api/challenges"]
  });

  const { data: progress } = useQuery<(UserChallengeProgress & { challenge: DailyChallenge })[]>({
    queryKey: ["/api/user/1/challenges"]
  });

  const currentChallenge = challenges?.[0];
  const currentProgress = progress?.[0];

  if (!currentChallenge || !currentProgress) {
    return null;
  }

  const progressPercentage = Math.min((currentProgress.progress / 3) * 100, 100); // Assuming requirement is 3

  return (
    <div className="bg-orange-200 p-3 text-center relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-20 animate-pulse"></div>
      <p className="text-sm font-semibold text-gray-800">
        {currentChallenge.emoji} Daily Quest: {currentChallenge.title} ({currentProgress.progress}/3)
      </p>
      <div className="w-full bg-white bg-opacity-50 rounded-full h-2 mt-1">
        <div 
          className="bg-pink-400 h-2 rounded-full transition-all duration-500" 
          style={{ width: `${progressPercentage}%` }}
        ></div>
      </div>
    </div>
  );
}

client/src/components/edit-profile-dialo.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Camera, Image, User } from "lucide-react";
import type { User as UserType } from "@shared/schema";

interface EditProfileDialogProps {
  user: UserType;
  children: React.ReactNode;
}

export default function EditProfileDialog({ user, children }: EditProfileDialogProps) {
  const [open, setOpen] = useState(false);
  const [formData, setFormData] = useState({
    username: user.username || "",
    email: user.email || "",
    firstName: user.firstName || "",
    lastName: user.lastName || "",
    avatar: user.avatar || "",
  });
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const updateMutation = useMutation({
    mutationFn: async (updates: Partial<UserType>) => {
      return apiRequest("PATCH", `/api/user/${user.id}`, updates);
    },
    onSuccess: () => {
      toast({
        title: "Profile updated! ✨",
        description: "Your changes have been saved successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
      setOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Update failed 😢",
        description: error.message || "Something went wrong. Try again!",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    updateMutation.mutate(formData);
  };

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  // iOS-compatible photo capture using Capacitor Camera
  const handleTakePhoto = async () => {
    try {
      setIsLoading(true);

      // Check if we're in Capacitor (iOS app)
      if (typeof window !== 'undefined' && (window as any).Capacitor) {
        const { Camera } = await import('@capacitor/camera');
        const { CameraResultType, CameraSource } = await import('@capacitor/camera');

        const image = await Camera.getPhoto({
          quality: 90,
          allowEditing: true,
          resultType: CameraResultType.DataUrl,
          source: CameraSource.Camera,
        });

        if (image.dataUrl) {
          handleChange('avatar', image.dataUrl);
          toast({
            title: "Photo captured! 📸",
            description: "Your new profile picture looks amazing!",
          });
        }
      } else {
        // Web fallback - file input
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';
        input.capture = 'environment';

        input.onchange = (e) => {
          const file = (e.target as HTMLInputElement).files?.[0];
          if (file) {
            const reader = new FileReader();
            reader.onload = (e) => {
              const dataUrl = e.target?.result as string;
              if (dataUrl) {
                handleChange('avatar', dataUrl);
                toast({
                  title: "Photo captured! 📸",
                  description: "Your new profile picture looks amazing!",
                });
              }
            };
            reader.readAsDataURL(file);
          }
        };

        input.click();
      }
    } catch (error) {

      toast({
        title: "Photo capture failed 😢",
        description: "Couldn't capture photo. Try again!",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  // iOS-compatible gallery selection
  const handleSelectFromGallery = async () => {
    try {
      setIsLoading(true);

      // Check if we're in Capacitor (iOS app)
      if (typeof window !== 'undefined' && (window as any).Capacitor) {
        const { Camera } = await import('@capacitor/camera');
        const { CameraResultType, CameraSource } = await import('@capacitor/camera');

        const image = await Camera.getPhoto({
          quality: 90,
          allowEditing: true,
          resultType: CameraResultType.DataUrl,
          source: CameraSource.Photos,
        });

        if (image.dataUrl) {
          handleChange('avatar', image.dataUrl);
          toast({
            title: "Photo selected! 📸",
            description: "Your new profile picture looks amazing!",
          });
        }
      } else {
        // Web fallback - file input
        const input = document.createElement('input');
        input.type = 'file';
        input.accept = 'image/*';

        input.onchange = (e) => {
          const file = (e.target as HTMLInputElement).files?.[0];
          if (file) {
            const reader = new FileReader();
            reader.onload = (e) => {
              const dataUrl = e.target?.result as string;
              if (dataUrl) {
                handleChange('avatar', dataUrl);
                toast({
                  title: "Photo selected! 📸",
                  description: "Your new profile picture looks amazing!",
                });
              }
            };
            reader.readAsDataURL(file);
          }
        };

        input.click();
      }
    } catch (error) {

      toast({
        title: "Photo selection failed 😢",
        description: "Couldn't select photo. Try again!",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        {children}
      </DialogTrigger>
      <DialogContent className="max-w-md mx-auto">
        <DialogHeader>
          <DialogTitle className="text-center text-xl font-bold text-pink-600">
            Edit Profile ✨
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Profile Picture */}
          <div className="flex flex-col items-center space-y-4">
            <Avatar className="w-24 h-24">
              <AvatarImage src={formData.avatar} alt={formData.username} />
              <AvatarFallback className="bg-pink-100 text-pink-600 text-2xl">
                {formData.firstName?.charAt(0)}{formData.lastName?.charAt(0)}
              </AvatarFallback>
            </Avatar>

            <div className="flex space-x-2">
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={handleTakePhoto}
                disabled={isLoading}
                className="flex items-center space-x-1"
              >
                <Camera className="w-4 h-4" />
                <span>Camera</span>
              </Button>
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={handleSelectFromGallery}
                disabled={isLoading}
                className="flex items-center space-x-1"
              >
                <Image className="w-4 h-4" />
                <span>Gallery</span>
              </Button>
            </div>
          </div>

          {/* Form Fields */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="firstName">First Name</Label>
              <Input
                id="firstName"
                value={formData.firstName}
                onChange={(e) => handleChange('firstName', e.target.value)}
                placeholder="Enter your first name"
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="lastName">Last Name</Label>
              <Input
                id="lastName"
                value={formData.lastName}
                onChange={(e) => handleChange('lastName', e.target.value)}
                placeholder="Enter your last name"
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="username">Username</Label>
              <Input
                id="username"
                value={formData.username}
                onChange={(e) => handleChange('username', e.target.value)}
                placeholder="Enter your username"
                className="mt-1"
              />
            </div>

            <div>
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                value={formData.email}
                onChange={(e) => handleChange('email', e.target.value)}
                placeholder="Enter your email"
                className="mt-1"
              />
            </div>
          </div>

          {/* Submit Buttons */}
          <div className="flex space-x-3">
            <Button
              type="button"
              variant="outline"
              onClick={() => setOpen(false)}
              className="flex-1"
              disabled={updateMutation.isPending}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              className="flex-1 bg-gradient-to-r from-pink-500 to-purple-500"
              disabled={updateMutation.isPending}
            >
              {updateMutation.isPending ? "Saving..." : "Save Changes"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

