client/src/components/permission-handler.content:
import { useEffect, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { MapPin, Camera, Bell } from "lucide-react";
import { useCapacitor } from "@/hooks/use-capacitor";

interface PermissionHandlerProps {
  children: React.ReactNode;
}

export default function PermissionHandler({ children }: PermissionHandlerProps) {
  const { isNative } = useCapacitor();
  const [permissionsGranted, setPermissionsGranted] = useState(false);
  const [locationPermission, setLocationPermission] = useState<'granted' | 'denied' | 'pending'>('pending');
  const [notificationPermission, setNotificationPermission] = useState<'granted' | 'denied' | 'pending'>('pending');

  useEffect(() => {
    checkPermissions();
  }, []);

  const checkPermissions = async () => {
    if (!isNative) {
      // Check location permission
      if ('geolocation' in navigator) {
        try {
          await new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(resolve, reject, { timeout: 5000 });
          });
          setLocationPermission('granted');
          setPermissionsGranted(true);
        } catch {
          setLocationPermission('pending');
          setPermissionsGranted(false);
        }
      }

      // Check notification permission
      if ('Notification' in window) {
        const permission = Notification.permission;
        setNotificationPermission(permission === 'granted' ? 'granted' : permission === 'denied' ? 'denied' : 'pending');
      }
    } else {
      // For native app, we still need to check and request location permission
      try {
        if ('geolocation' in navigator) {
          await new Promise((resolve, reject) => {
            navigator.geolocation.getCurrentPosition(resolve, reject, { timeout: 5000 });
          });
          setLocationPermission('granted');
          setPermissionsGranted(true);
        } else {
          // Use Capacitor for native geolocation
          const { Geolocation } = await import('@capacitor/geolocation');
          const permissions = await Geolocation.requestPermissions();

          if (permissions.location === 'granted') {
            setLocationPermission('granted');
            setPermissionsGranted(true);
          } else {
            setLocationPermission('pending');
            setPermissionsGranted(false);
          }
        }
      } catch (error) {
        // Native location permission error
        setLocationPermission('pending');
        setPermissionsGranted(false);
      }
    }
  };

  const requestLocationPermission = async () => {
    try {
      if (isNative) {
        // Use Capacitor for native apps
        const { Geolocation } = await import('@capacitor/geolocation');
        const permissions = await Geolocation.requestPermissions();

        if (permissions.location === 'granted') {
          // Test actual location access
          await Geolocation.getCurrentPosition({
            enableHighAccuracy: true,
            timeout: 10000,
          });
          setLocationPermission('granted');
          setPermissionsGranted(true);
        } else {
          setLocationPermission('denied');
        }
      } else {
        // Use browser geolocation for web
        if (!('geolocation' in navigator)) {
          setLocationPermission('denied');
          return;
        }

        await new Promise((resolve, reject) => {
          navigator.geolocation.getCurrentPosition(resolve, reject, { 
            timeout: 10000,
            enableHighAccuracy: true 
          });
        });
        setLocationPermission('granted');
        setPermissionsGranted(true);
      }
    } catch (error) {
      // Location permission error
      setLocationPermission('denied');
    }
  };

  const requestNotificationPermission = async () => {
    if (!('Notification' in window)) {
      setNotificationPermission('denied');
      return;
    }

    try {
      const permission = await Notification.requestPermission();
      setNotificationPermission(permission === 'granted' ? 'granted' : 'denied');
    } catch (error) {
      // Notification permission error
      setNotificationPermission('denied');
    }
  };

  // Show permission screen for web users
  if (!isNative && !permissionsGranted && locationPermission !== 'granted') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-pink-50 via-white to-purple-50 flex items-center justify-center p-4">
        <Card className="w-full max-w-md card-gradient rounded-2xl shadow-2xl border-0">
          <CardHeader className="text-center space-y-2 pb-6">
            <div className="mx-auto w-16 h-16 bg-gradient-to-r from-pink-400 to-purple-500 rounded-full flex items-center justify-center text-white font-bold text-2xl mb-4">
              S
            </div>
            <CardTitle className="text-3xl font-bold bg-gradient-to-r from-pink-500 to-purple-600 bg-clip-text text-transparent">
              Enable Permissions
            </CardTitle>
            <p className="text-gray-600">
              Socialiser needs a few permissions to help you discover amazing spots
            </p>
          </CardHeader>

          <CardContent className="space-y-6">
            <div className="space-y-4">
              <div className="flex items-center space-x-4 p-4 bg-pink-50 rounded-xl">
                <MapPin className="w-6 h-6 text-pink-500" />
                <div className="flex-1">
                  <h3 className="font-semibold text-gray-900">Location Access</h3>
                  <p className="text-sm text-gray-600">Find trendy spots near you and verify check-ins</p>
                </div>
                {locationPermission === 'granted' && <span className="text-green-500">✓</span>}
              </div>

              <div className="flex items-center space-x-4 p-4 bg-purple-50 rounded-xl">
                <Bell className="w-6 h-6 text-purple-500" />
                <div className="flex-1">
                  <h3 className="font-semibold text-gray-900">Notifications</h3>
                  <p className="text-sm text-gray-600">Get alerted about trending spots nearby</p>
                </div>
                {notificationPermission === 'granted' && <span className="text-green-500">✓</span>}
              </div>
            </div>

            {locationPermission !== 'granted' && (
              <Button
                onClick={requestLocationPermission}
                className="w-full bg-gradient-to-r from-pink-400 to-purple-500 text-white rounded-xl py-3 text-lg font-semibold hover:shadow-lg transition-all"
              >
                Enable Location Access
              </Button>
            )}

            {locationPermission === 'granted' && notificationPermission === 'pending' && (
              <Button
                onClick={requestNotificationPermission}
                className="w-full bg-gradient-to-r from-purple-400 to-pink-500 text-white rounded-xl py-3 text-lg font-semibold hover:shadow-lg transition-all"
              >
                Enable Notifications (Optional)
              </Button>
            )}

            {locationPermission === 'granted' && (
              <Button
                onClick={() => setPermissionsGranted(true)}
                className="w-full bg-gradient-to-r from-green-400 to-blue-500 text-white rounded-xl py-3 text-lg font-semibold hover:shadow-lg transition-all"
              >
                Continue to App
              </Button>
            )}

            <p className="text-center text-xs text-gray-500">
              These permissions help us provide the best spot discovery experience. You can change them anytime in your browser settings.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return <>{children}</>;
}

client/src/components/progress-hints.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Target, Star, Trophy, Zap } from "lucide-react";

interface ProgressHintProps {
  current: number;
  target: number;
  label: string;
  icon: React.ReactNode;
  nextReward: string;
}

export function ProgressHint({ current, target, label, icon, nextReward }: ProgressHintProps) {
  const percentage = Math.min((current / target) * 100, 100);
  const isComplete = current >= target;

  return (
    <Card className="bg-gradient-to-r from-purple-50 to-pink-50 border-purple-100">
      <CardContent className="p-3">
        <div className="flex items-center justify-between mb-2">
          <div className="flex items-center space-x-2">
            {icon}
            <span className="text-sm font-medium text-gray-800">{label}</span>
          </div>
          <Badge variant={isComplete ? "default" : "secondary"} className="text-xs">
            {current}/{target}
          </Badge>
        </div>
        <Progress value={percentage} className="h-2 mb-2" />
        <p className="text-xs text-gray-600">
          {isComplete ? "🎉 Complete!" : `Next: ${nextReward}`}
        </p>
      </CardContent>
    </Card>
  );
}

export function NextBadgeHint({ spotsHunted }: { spotsHunted: number }) {
  const hints = [
    { spots: 1, badge: "First Hunt Cutie", description: "Check into your first spot" },
    { spots: 5, badge: "Explorer Babe", description: "Discover 5 different spots" },
    { spots: 10, badge: "Local Legend", description: "Hunt 10 spots like a pro" },
    { spots: 25, badge: "Trendsetter", description: "Find 25 amazing places" },
    { spots: 50, badge: "Ultimate Hunter", description: "Master of spot discovery" }
  ];

  const nextHint = hints.find(h => spotsHunted < h.spots);
  if (!nextHint) return null;

  return (
    <ProgressHint
      current={spotsHunted}
      target={nextHint.spots}
      label="Spots Progress"
      icon={<Target className="w-4 h-4 text-purple-500" />}
      nextReward={`${nextHint.badge} badge`}
    />
  );
}

export function StreakProgress({ currentStreak }: { currentStreak: number }) {
  const nextMilestone = currentStreak < 3 ? 3 : currentStreak < 7 ? 7 : currentStreak < 30 ? 30 : null;

  if (!nextMilestone) return null;

  return (
    <ProgressHint
      current={currentStreak}
      target={nextMilestone}
      label="Daily Streak"
      icon={<Zap className="w-4 h-4 text-yellow-500" />}
      nextReward={`${nextMilestone} day streak reward`}
    />
  );
}

client/src/components/pull-to-refresh-in.content:
import { motion } from "framer-motion";
import { RefreshCw } from "lucide-react";

interface PullToRefreshIndicatorProps {
  pullDistance: number;
  pullProgress: number;
  isRefreshing: boolean;
}

export function PullToRefreshIndicator({ 
  pullDistance, 
  pullProgress, 
  isRefreshing 
}: PullToRefreshIndicatorProps) {
  if (pullDistance === 0 && !isRefreshing) return null;

  return (
    <motion.div
      className="fixed top-0 left-0 right-0 z-50 flex justify-center pt-safe"
      initial={{ y: -60 }}
      animate={{ y: pullDistance > 0 || isRefreshing ? 0 : -60 }}
      transition={{ type: "spring", stiffness: 300, damping: 30 }}
    >
      <div 
        className="bg-white/90 backdrop-blur-sm rounded-full shadow-lg p-3 flex items-center space-x-2"
        style={{
          transform: `scale(${0.7 + pullProgress * 0.3})`,
          opacity: pullProgress
        }}
      >
        <RefreshCw 
          className={`w-5 h-5 text-purple-500 ${isRefreshing ? 'animate-spin' : ''}`}
          style={{
            transform: `rotate(${pullProgress * 180}deg)`
          }}
        />
        <span className="text-sm font-medium text-purple-600">
          {isRefreshing ? 'Refreshing...' : pullProgress >= 1 ? 'Release to refresh' : 'Pull to refresh'}
        </span>
      </div>
    </motion.div>
  );
}

