import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { useCamera } from "@/hooks/useCamera";
import { useCreateStory } from "@/hooks/useStories";
import { Camera, X, Plus } from "lucide-react";

// Check if we're in iOS native app
const isIOSNative = typeof window !== 'undefined' && (window as any).Capacitor;

// Simple social media components
export function VerticalFeed({ children }: { children: React.ReactNode }) {
  return <div className="space-y-4">{children}</div>;
}

export function ExploreGrid({ children }: { children: React.ReactNode }) {
  return <div className="grid grid-cols-2 gap-4">{children}</div>;
}

export function UpdatesFeed({ children }: { children: React.ReactNode }) {
  return <div className="space-y-3">{children}</div>;
}

export function DoubleTapLike({ children }: { children: React.ReactNode }) {
  return <div>{children}</div>;
}

export function PullToRefreshContainer({ children }: { children: React.ReactNode }) {
  return <div>{children}</div>;
}

interface StoriesStripProps {
  onCreateStory: () => void;
}

export function StoriesStrip({ onCreateStory }: StoriesStripProps) {
  const [showCreateStory, setShowCreateStory] = useState(false);
  const [storyImage, setStoryImage] = useState<string>("");
  const [storyCaption, setStoryCaption] = useState("");
  const { toast } = useToast();
  const { choosePhotoSource } = useCamera();

  // Use the new native-compatible story creation hook
  const createStoryMutation = useCreateStory();

  const handleTakePhoto = async () => {
    try {
      const imageDataUrl = await choosePhotoSource();

      if (imageDataUrl) {
        setStoryImage(imageDataUrl);

        toast({
          title: "Photo captured! 📸",
          description: isIOSNative ? "Add a caption to save your story locally" : "Add a caption and post your story.",
        });
      }
    } catch (error) {
      toast({
        title: "Photo failed",
        description: "Please try taking a photo again!",
        variant: "destructive",
      });
    }
  };

  const handleCreateStory = () => {
    if (!storyImage) {
      toast({
        title: "Photo required!",
        description: "Take a photo first to create your story.",
        variant: "destructive",
      });
      return;
    }

    const storyData = {
      userId: "1",
      imageUrl: storyImage,
      caption: storyCaption || "",
      type: "photo" as const,
      expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000) // 24 hours
    };

    createStoryMutation.mutate(storyData, {
      onSuccess: () => {
        // Reset the form
        setStoryImage("");
        setStoryCaption("");
        setShowCreateStory(false);
      }
    });
  };

  return (
    <>
      {/* Stories strip */}
      <div className="flex space-x-3 p-4 overflow-x-auto">
        {/* Add your story button */}
        <div className="flex flex-col items-center space-y-2 min-w-fit">
          <button
            onClick={() => setShowCreateStory(true)}
            className="w-16 h-16 rounded-full bg-gradient-to-br from-pink-400 to-purple-500 flex items-center justify-center shadow-lg border-2 border-white"
          >
            <Plus className="w-6 h-6 text-white" />
          </button>
          <span className="text-xs text-gray-600 text-center">Your Story</span>
        </div>
      </div>

      {/* Create Story Dialog */}
      <Dialog open={showCreateStory} onOpenChange={setShowCreateStory}>
        <DialogContent className="max-w-md mx-auto">
          <DialogHeader>
            <DialogTitle className="text-center text-xl font-bold text-pink-600">
              {isIOSNative ? "Save Story Locally ✨" : "Create Story ✨"}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-6">
            {/* Photo Preview */}
            {storyImage ? (
              <div className="relative">
                <img 
                  src={storyImage} 
                  alt="Story preview" 
                  className="w-full h-64 object-cover rounded-lg"
                />
                <button
                  onClick={() => setStoryImage("")}
                  className="absolute top-2 right-2 p-1 bg-red-500 text-white rounded-full hover:bg-red-600"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
            ) : (
              <div className="w-full h-64 bg-gray-100 rounded-lg flex items-center justify-center">
                <div className="text-center">
                  <Camera className="w-12 h-12 text-gray-400 mx-auto mb-2" />
                  <p className="text-gray-500">No photo selected</p>
                </div>
              </div>
            )}

            {/* Photo Actions */}
            <div className="flex gap-2">
              <Button
                onClick={handleTakePhoto}
                variant="outline"
                className="flex-1"
                disabled={createStoryMutation.isPending}
              >
                <Camera className="w-4 h-4 mr-2" />
                {storyImage ? "Retake Photo" : "Take Photo"}
              </Button>
            </div>

            {/* Caption Input */}
            <div className="space-y-2">
              <label className="text-sm font-medium text-gray-700">
                Caption (optional)
              </label>
              <Textarea
                placeholder={isIOSNative ? "Add a caption for your local story..." : "What's happening?"}
                value={storyCaption}
                onChange={(e) => setStoryCaption(e.target.value)}
                className="min-h-20"
                maxLength={280}
              />
              <p className="text-xs text-gray-500 text-right">
                {storyCaption.length}/280 characters
              </p>
            </div>

            {/* Post Button */}
            <Button
              onClick={handleCreateStory}
              disabled={!storyImage || createStoryMutation.isPending}
              className="w-full bg-gradient-to-r from-pink-500 to-purple-600 hover:from-pink-600 hover:to-purple-700"
            >
              {createStoryMutation.isPending ? (
                "Saving..."
              ) : isIOSNative ? (
                "Save Story Locally"
              ) : (
                "Post Story"
              )}
            </Button>

            {isIOSNative && (
              <p className="text-xs text-gray-500 text-center">
                Stories are saved locally on your device for 24 hours
              </p>
            )}
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/spot-card.tsx.content:
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Target, Star, Route, MapPin, Navigation } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useGeolocation } from "@/hooks/use-geolocation";
import { useHaptics } from "@/hooks/use-haptics";
import { formatDistance, calculateDistance, isWithinRange } from "@/lib/location-utils";
import { getSpotIcon } from "@/lib/spot-icons";
import { CelebrationAnimation } from "./celebration-animation";
import { useState, memo } from "react";
import type { Spot } from "@shared/schema";

interface SpotCardProps {
  spot: Spot;
}



function SpotCard({ spot }: SpotCardProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { latitude, longitude, loading: locationLoading, error: locationError } = useGeolocation();
  const { triggerHaptic } = useHaptics();
  const [showCelebration, setShowCelebration] = useState(false);
  const [celebrationData, setCelebrationData] = useState<any>(null);

  // Calculate distance to spot
  const distance = latitude && longitude 
    ? calculateDistance(latitude, longitude, spot.latitude, spot.longitude)
    : null;

  const withinRange = distance ? isWithinRange(latitude!, longitude!, spot.latitude, spot.longitude, 100) : false;

  const huntMutation = useMutation({
    mutationFn: async () => {
      if (!latitude || !longitude) {
        throw new Error("Location required for check-in");
      }
      return apiRequest("POST", `/api/spots/${spot.id}/hunt`, { 
        userId: 1, 
        userLatitude: latitude,
        userLongitude: longitude,
        spotData: {
          name: spot.name,
          description: spot.description,
          category: spot.category,
          latitude: spot.latitude,
          longitude: spot.longitude,
          address: spot.address,
          rating: spot.rating,
          imageUrl: spot.imageUrl,
          priceRange: spot.priceRange,
          dietaryOptions: spot.dietaryOptions,
          ambiance: spot.ambiance,
          amenities: spot.amenities
        }
      });
    },
    onSuccess: (data: any) => {
      // Trigger success haptic feedback
      triggerHaptic('success');

      // Store celebration data and trigger amazing animation
      setCelebrationData({
        pointsEarned: data.pointsEarned || 50,
        newBadges: data.newBadges || [],
        spotName: spot.name,
        totalPoints: data.totalPoints,
        spotsHunted: data.spotsHunted
      });
      setShowCelebration(true);

      // Invalidate all relevant caches to update UI
      queryClient.invalidateQueries({ queryKey: ["/api/spots"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/1"] });
      queryClient.invalidateQueries({ queryKey: ["/api/user/1/badges"] });
      queryClient.invalidateQueries({ queryKey: ["/api/activity"] });
    },
    onError: (error: any) => {
      const errorData = error.response?.data;
      if (errorData?.message === "Too far from location") {
        toast({
          title: "Too far away! 📍",
          description: errorData.details || "You need to be closer to check in",
          variant: "destructive",
        });
      } else if (errorData?.message === "Location data required for check-in") {
        toast({
          title: "Location needed 📱",
          description: "Please allow location access to check in",
          variant: "destructive",
        });
      } else {
        toast({
          title: "Hunt failed 😢",
          description: "Something went wrong. Try again!",
          variant: "destructive",
        });
      }
    },
  });

  const getDistanceDisplay = () => {
    if (distance !== null) {
      return formatDistance(distance);
    }
    if (locationLoading) {
      return "Loading...";
    }
    if (locationError) {
      return "Location unavailable";
    }
    return "Unknown distance";
  };

  const openDirections = () => {
    // Create a comprehensive search query with location details
    const spotName = spot.name;
    const spotAddress = spot.address || '';

    // Build search query prioritizing recognizable place names
    let searchQuery = spotName;

    // Add address if available
    if (spotAddress && spotAddress.trim() !== '') {
      searchQuery = `${spotName}, ${spotAddress}`;
    } else {
      // If no address, add city context for better search results
      searchQuery = `${spotName}, London`; // Default to London for context
    }

    const encodedQuery = encodeURIComponent(searchQuery);

    // Detect mobile for native app integration
    const isMobile = /Android|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent);
    const isIOS = /iPad|iPhone|iPod/.test(navigator.userAgent);

    try {
      if (isMobile) {
        if (isIOS) {
          // iOS - try Apple Maps first, fallback to Google Maps
          const appleMapsUrl = `https://maps.apple.com/?q=${encodedQuery}&dirflg=d`;
          window.location.href = appleMapsUrl;

          // Fallback to Google Maps if Apple Maps doesn't work
          setTimeout(() => {
            const googleMapsUrl = `https://maps.google.com/maps?q=${encodedQuery}&navigate=yes`;
            window.open(googleMapsUrl, '_blank');
          }, 2000);
        } else {
          // Android - use Google Maps directly
          const googleMapsUrl = `https://maps.google.com/maps?q=${encodedQuery}&navigate=yes`;
          window.location.href = googleMapsUrl;
        }
      } else {
        // Desktop - open Google Maps in new tab with proper search query
        const googleMapsUrl = `https://www.google.com/maps/search/${encodedQuery}`;
        window.open(googleMapsUrl, '_blank');
      }

      toast({
        title: "Opening navigation",
        description: `Getting directions to ${spotName}`,
      });

    } catch (error) {

      toast({
        title: "Navigation error",
        description: "Unable to open directions",
        variant: "destructive",
      });
    }
  };

  return (
    <div 
      className="spot-card bg-white rounded-xl p-3 shadow-md border border-purple-100"
      style={{
        WebkitTapHighlightColor: 'rgba(236, 72, 153, 0.1)',
        touchAction: 'manipulation'
      }}
    >
      <div className="flex items-center space-x-3">
        <div className="w-16 h-16 rounded-xl bg-gradient-to-br from-purple-100 to-pink-100 flex items-center justify-center">
          {getSpotIcon(spot)}
        </div>
        <div className="flex-1">
          <div className="flex items-center space-x-1">
            <h3 className="font-semibold text-gray-800 text-sm">{spot.name}</h3>
            {spot.trending && <span className="text-xs">🔥</span>}
          </div>
          <p className="text-xs text-gray-600">{spot.description}</p>
          <div className="flex items-center space-x-2 mt-1">
            <span className={`text-xs px-2 py-1 rounded-full ${
              withinRange 
                ? "bg-green-100 text-green-700" 
                : !latitude || !longitude
                ? "bg-gray-100 text-gray-600"
                : "bg-red-100 text-red-700"
            }`}>
              {getDistanceDisplay()}
              {withinRange && " ✓"}
              {distance && distance > 100 && " ⚠️"}
            </span>
            <div className="flex items-center space-x-1">
              <Star className="w-3 h-3 fill-yellow-400 text-yellow-400" />
              <span className="text-xs text-gray-600">{spot.rating}</span>
            </div>
            <span className="text-xs text-gray-500">{spot.huntCount} hunts</span>
          </div>
        </div>
        <div className="flex space-x-2">
          {/* Directions Button */}
          <Button 
            className="bg-blue-500 hover:bg-blue-600 text-white p-3 rounded-full shadow-lg min-h-[44px] min-w-[44px] transition-transform active:scale-95"
            onClick={openDirections}
            disabled={!latitude || !longitude}
            title="Get directions"
            style={{
              WebkitTapHighlightColor: 'rgba(59, 130, 246, 0.3)',
              touchAction: 'manipulation'
            }}
          >
            <Route className="w-4 h-4" />
          </Button>

          {/* Hunt Button */}
          <Button 
            className={`${
              withinRange 
                ? "bg-green-500 hover:bg-green-600" 
                : !latitude || !longitude
                ? "bg-gray-400"
                : "bg-red-400 hover:bg-red-500"
            } text-white p-3 rounded-full shadow-lg disabled:opacity-50 min-h-[44px] min-w-[44px] transition-transform active:scale-95`}
            onClick={() => {
              triggerHaptic('medium');
              huntMutation.mutate();
            }}
            disabled={huntMutation.isPending || !withinRange || !latitude || !longitude}
            style={{
              WebkitTapHighlightColor: withinRange ? 'rgba(34, 197, 94, 0.3)' : 'rgba(248, 113, 113, 0.3)',
              touchAction: 'manipulation'
            }}
            title={
              !latitude || !longitude 
                ? "Location needed" 
                : withinRange 
                ? "In range - tap to hunt!" 
                : `Too far away (${distance ? Math.round(distance) : '?'}m)`
            }
          >
            {!latitude || !longitude ? (
              <MapPin className="w-4 h-4" />
            ) : withinRange ? (
              <Target className="w-4 h-4" />
            ) : (
              <Navigation className="w-4 h-4" />
            )}
          </Button>
        </div>
      </div>

      {/* Amazing Celebration Animation */}
      {celebrationData && (
        <CelebrationAnimation
          show={showCelebration}
          onComplete={() => {
            setShowCelebration(false);
            setCelebrationData(null);
          }}
          pointsEarned={celebrationData.pointsEarned}
          newBadges={celebrationData.newBadges}
          spotName={celebrationData.spotName}
        />
      )}
    </div>
  );
}

// Memoize the component for better performance
export default memo(SpotCard);

