import { useState } from 'react';
import { Camera, CameraResultType, CameraSource } from '@capacitor/camera';
import { useCapacitor } from './use-capacitor';

interface CameraOptions {
  source?: CameraSource;
  quality?: number;
  allowEditing?: boolean;
  resultType?: CameraResultType;
}

export function useCamera() {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const { isNative } = useCapacitor();

  const takePicture = async (options: CameraOptions = {}) => {
    setIsLoading(true);
    setError(null);

    try {
      const defaultOptions = {
        quality: 90,
        allowEditing: true,
        resultType: CameraResultType.DataUrl,
        source: CameraSource.Prompt, // This allows choosing camera or gallery
        ...options
      };

      if (isNative) {
        // Use native camera on mobile
        const image = await Camera.getPhoto(defaultOptions);
        setIsLoading(false);
        return image.dataUrl;
      } else {
        // Use web file input as fallback
        return new Promise<string>((resolve, reject) => {
          const input = document.createElement('input');
          input.type = 'file';
          input.accept = 'image/*';
          input.capture = 'environment'; // Use rear camera by default

          input.onchange = (e) => {
            const file = (e.target as HTMLInputElement).files?.[0];
            if (file) {
              const reader = new FileReader();
              reader.onload = (e) => {
                const result = e.target?.result as string;
                setIsLoading(false);
                resolve(result);
              };
              reader.onerror = () => {
                setIsLoading(false);
                reject(new Error('Failed to read image'));
              };
              reader.readAsDataURL(file);
            } else {
              setIsLoading(false);
              reject(new Error('No file selected'));
            }
          };

          input.click();
        });
      }
    } catch (err) {
      setIsLoading(false);
      const errorMessage = err instanceof Error ? err.message : 'Failed to capture image';
      setError(errorMessage);
      throw new Error(errorMessage);
    }
  };

  const selectFromGallery = async () => {
    return takePicture({ source: CameraSource.Photos });
  };

  const takePhoto = async () => {
    return takePicture({ source: CameraSource.Camera });
  };

  const choosePhotoSource = async () => {
    return takePicture({ source: CameraSource.Prompt });
  };

  return {
    takePicture,
    selectFromGallery,
    takePhoto,
    choosePhotoSource,
    isLoading,
    error,
    isNative
  };
}

client/src/hooks/use-capacitor.ts.content:
import { useEffect, useState } from 'react';
import { App } from '@capacitor/app';
// StatusBar plugin removed for iOS compatibility
import { SplashScreen } from '@capacitor/splash-screen';
import { Device } from '@capacitor/device';

export function useCapacitor() {
  const [isNative, setIsNative] = useState(false);
  const [deviceInfo, setDeviceInfo] = useState<any>(null);

  useEffect(() => {
    const initCapacitor = async () => {
      try {
        // Check if running in native app
        const info = await Device.getInfo();
        setDeviceInfo(info);
        setIsNative(info.platform !== 'web');

        if (info.platform !== 'web') {
          // StatusBar configuration removed for iOS compatibility

          // Hide splash screen after app loads
          await SplashScreen.hide();

          // Handle app state changes
          App.addListener('appStateChange', ({ isActive }) => {

          });

          // Handle back button on Android
          App.addListener('backButton', ({ canGoBack }) => {
            if (!canGoBack) {
              App.exitApp();
            } else {
              window.history.back();
            }
          });
        }
      } catch (error) {

        setIsNative(false);
      }
    };

    initCapacitor();

    return () => {
      // Cleanup listeners
      App.removeAllListeners();
    };
  }, []);

  return {
    isNative,
    deviceInfo,
  };
}

client/src/hooks/use-geolocation.ts.content:
import { useState, useEffect } from 'react';
import { useCapacitor } from './use-capacitor';

// Import Capacitor Geolocation for native GPS access
declare global {
  interface Window {
    Capacitor?: {
      Geolocation?: {
        getCurrentPosition: (options?: any) => Promise<any>;
      };
      isNativePlatform: () => boolean;
      platform?: string;
    };
  }
}

interface LocationState {
  latitude: number | null;
  longitude: number | null;
  accuracy: number | null;
  loading: boolean;
  error: string | null;
}

// iOS and native detection helper
const isIOSNative = () => {
  return (window as any).Capacitor?.isNativePlatform() || 
         (window as any).Capacitor?.platform === 'ios' ||
         (window as any).Device?.info?.platform === 'ios' ||
         window.navigator.userAgent.includes('iPhone') ||
         window.navigator.userAgent.includes('iPad');
};

export function useGeolocation() {
  const { isNative } = useCapacitor();
  const [location, setLocation] = useState<LocationState>({
    latitude: null,
    longitude: null,
    accuracy: null,
    loading: true,
    error: null,
  });

  const getCurrentPosition = async () => {
    try {
      setLocation(prev => ({ ...prev, loading: true, error: null }));

      const isNativeOrIOS = isIOSNative() || isNative;

      // Try Capacitor native geolocation first for iOS apps
      if (isNativeOrIOS && (window as any).Capacitor?.Geolocation) {
        try {
          console.log('[iOS] Using Capacitor native geolocation');
          const position = await (window as any).Capacitor.Geolocation.getCurrentPosition({
            enableHighAccuracy: true,
            timeout: 10000,
            maximumAge: 30000
          });

          console.log(`[iOS] Capacitor GPS coordinates: ${position.coords.latitude}, ${position.coords.longitude}`);
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            accuracy: position.coords.accuracy,
            loading: false,
            error: null,
          });
          return;
        } catch (capacitorError) {
          console.log('[iOS] Capacitor geolocation failed, trying web API');
        }
      }

      // Fallback to web geolocation API
      if (!navigator.geolocation) {
        throw new Error('Geolocation is not supported by this browser');
      }

      const timeout = isNativeOrIOS ? 8000 : 10000; // Timeout for native apps

      navigator.geolocation.getCurrentPosition(
        (position) => {
          console.log(`[iOS] GPS coordinates received: ${position.coords.latitude}, ${position.coords.longitude}`);
          setLocation({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude,
            accuracy: position.coords.accuracy,
            loading: false,
            error: null,
          });
        },
        (error) => {
          // For native/iOS apps, fallback to London coordinates if GPS fails
          if (isNativeOrIOS) {
            console.log(`[iOS] GPS failed, using fallback coordinates: 51.511153, -0.273239`);
            setLocation({
              latitude: 51.511153,
              longitude: -0.273239,
              accuracy: 100,
              loading: false,
              error: 'Using default location - enable GPS for real-time spots',
            });
            return;
          }

          let errorMessage = 'Location error';
          switch (error.code) {
            case error.PERMISSION_DENIED:
              errorMessage = 'Location permission denied';
              break;
            case error.POSITION_UNAVAILABLE:
              errorMessage = 'Location information unavailable';
              break;
            case error.TIMEOUT:
              errorMessage = 'Location request timed out';
              break;
          }
          setLocation(prev => ({
            ...prev,
            loading: false,
            error: errorMessage,
          }));
        },
        {
          enableHighAccuracy: true,
          timeout: timeout,
          maximumAge: 30000, // Shorter cache for more real-time updates
        }
      );
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Location error';
      setLocation(prev => ({
        ...prev,
        loading: false,
        error: errorMessage,
      }));
    }
  };

  useEffect(() => {
    getCurrentPosition();
  }, [isNative]);

  return {
    ...location,
    refetch: getCurrentPosition,
  };
}

client/src/hooks/use-google-maps.ts.content:
import { useState, useEffect, useCallback } from 'react';
import { useQuery } from '@tanstack/react-query';
import { GoogleMapsService, GoogleMapsPlace } from '@/lib/google-maps';

interface GoogleMapsConfig {
  googleMapsApiKey: string;
}

export function useGoogleMaps() {
  const [mapsService, setMapsService] = useState<GoogleMapsService | null>(null);
  const [map, setMap] = useState<any>(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Get API key from server
  const { data: config } = useQuery<GoogleMapsConfig>({
    queryKey: ["/api/config"],
    staleTime: Infinity, // Config rarely changes
  });

  useEffect(() => {
    if (config?.googleMapsApiKey && !mapsService) {
      const service = new GoogleMapsService(config.googleMapsApiKey);
      setMapsService(service);
    }
  }, [config, mapsService]);

  const initializeMap = useCallback(async (
    container: HTMLElement,
    center: { lat: number; lng: number }
  ) => {
    if (!mapsService) {
      setError('Google Maps service not available');
      return null;
    }

    try {
      setError(null);
      const mapInstance = await mapsService.initializeMap(container, center);
      setMap(mapInstance);
      setIsLoaded(true);
      return mapInstance;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to initialize map');
      return null;
    }
  }, [mapsService]);

  const findNearbySpots = useCallback(async (
    location: { lat: number; lng: number },
    radius: number = 2000
  ): Promise<GoogleMapsPlace[]> => {
    if (!mapsService) {
      throw new Error('Google Maps service not available');
    }

    try {
      setError(null);
      return await mapsService.findNearbyTrendySpots(location, radius);
    } catch (err) {
      const errorMsg = err instanceof Error ? err.message : 'Failed to find nearby spots';
      setError(errorMsg);
      throw new Error(errorMsg);
    }
  }, [mapsService]);

  const addMarker = useCallback((place: GoogleMapsPlace) => {
    if (!mapsService) return null;
    return mapsService.addMarker(place);
  }, [mapsService]);

  return {
    isLoaded,
    error,
    mapsService,
    map,
    initializeMap,
    findNearbySpots,
    addMarker,
    hasApiKey: !!config?.googleMapsApiKey
  };
}

client/src/hooks/use-haptics.ts.content:
import { useCallback } from "react";

export function useHaptics() {
  const triggerHaptic = useCallback((type: 'light' | 'medium' | 'heavy' | 'success' | 'warning' | 'error') => {
    if (typeof window !== 'undefined' && 'navigator' in window && 'vibrate' in navigator) {
      // Progressive enhancement for different feedback types
      const patterns = {
        light: [10],
        medium: [20],
        heavy: [30],
        success: [10, 10, 10],
        warning: [20, 10, 20],
        error: [50, 10, 50]
      };

      navigator.vibrate(patterns[type]);
    }
  }, []);

  return { triggerHaptic };
}

client/src/hooks/use-location.ts.content:
import { useState, useEffect } from 'react';
import { useGeolocation } from './use-geolocation';

interface LocationInfo {
  city: string | null;
  country: string | null;
  loading: boolean;
  error: string | null;
}

export function useLocation() {
  const { latitude, longitude, loading: geoLoading, error: geoError } = useGeolocation();
  const [locationInfo, setLocationInfo] = useState<LocationInfo>({
    city: null,
    country: null,
    loading: true,
    error: null
  });

  useEffect(() => {
    if (latitude && longitude && !geoLoading) {
      reverseGeocode(latitude, longitude);
    } else if (geoError) {
      setLocationInfo(prev => ({ ...prev, loading: false, error: geoError }));
    }
  }, [latitude, longitude, geoLoading, geoError]);

  const reverseGeocode = async (lat: number, lng: number) => {
    try {
      // Use OpenStreetMap Nominatim API for reverse geocoding (free)
      const response = await fetch(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&zoom=10&addressdetails=1`
      );

      if (!response.ok) {
        throw new Error('Failed to fetch location');
      }

      const data = await response.json();

      if (data && data.address) {
        const address = data.address;
        const city = address.city || address.town || address.village || address.municipality;
        const country = address.country;

        setLocationInfo({
          city,
          country,
          loading: false,
          error: null
        });
      } else {
        throw new Error('Location not found');
      }
    } catch (error) {
      setLocationInfo({
        city: null,
        country: null,
        loading: false,
        error: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  };

  return {
    ...locationInfo,
    loading: geoLoading || locationInfo.loading,
    error: geoError || locationInfo.error
  };
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-pull-to-refresh.ts.content:
import { useCallback, useEffect, useRef, useState } from "react";
import { useQueryClient } from "@tanstack/react-query";
import { useHaptics } from "./use-haptics";

export function usePullToRefresh() {
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [pullDistance, setPullDistance] = useState(0);
  const queryClient = useQueryClient();
  const { triggerHaptic } = useHaptics();
  const startY = useRef(0);
  const pullThreshold = 80;

  const handleTouchStart = useCallback((e: TouchEvent) => {
    if (window.scrollY === 0) {
      startY.current = e.touches[0].clientY;
    }
  }, []);

  const handleTouchMove = useCallback((e: TouchEvent) => {
    if (window.scrollY === 0 && startY.current > 0) {
      const currentY = e.touches[0].clientY;
      const diff = currentY - startY.current;

      if (diff > 0) {
        e.preventDefault();
        setPullDistance(Math.min(diff, pullThreshold * 1.5));

        // Haptic feedback when reaching threshold
        if (diff >= pullThreshold && pullDistance < pullThreshold) {
          triggerHaptic('medium');
        }
      }
    }
  }, [pullThreshold, pullDistance, triggerHaptic]);

  const handleTouchEnd = useCallback(async () => {
    if (pullDistance >= pullThreshold && !isRefreshing) {
      setIsRefreshing(true);
      triggerHaptic('success');

      // Refresh all cached data
      await queryClient.invalidateQueries();

      // Add a small delay for better UX
      setTimeout(() => {
        setIsRefreshing(false);
        setPullDistance(0);
      }, 1000);
    } else {
      setPullDistance(0);
    }
    startY.current = 0;
  }, [pullDistance, pullThreshold, isRefreshing, queryClient, triggerHaptic]);

  useEffect(() => {
    document.addEventListener('touchstart', handleTouchStart);
    document.addEventListener('touchmove', handleTouchMove, { passive: false });
    document.addEventListener('touchend', handleTouchEnd);

    return () => {
      document.removeEventListener('touchstart', handleTouchStart);
      document.removeEventListener('touchmove', handleTouchMove);
      document.removeEventListener('touchend', handleTouchEnd);
    };
  }, [handleTouchStart, handleTouchMove, handleTouchEnd]);

  return {
    isRefreshing,
    pullDistance,
    pullProgress: Math.min(pullDistance / pullThreshold, 1)
  };
}

client/src/hooks/use-push-notifications..content:
import { useEffect, useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useGeolocation } from "./use-geolocation";
import type { Notification } from "@shared/schema";

export function usePushNotifications(userId: string = "1") {
  const [isEnabled, setIsEnabled] = useState(true);
  const { latitude, longitude } = useGeolocation();
  const queryClient = useQueryClient();

  // Get user notifications
  const { data: notifications, isLoading } = useQuery<Notification[]>({
    queryKey: ["/api/user", userId, "notifications"],
    refetchInterval: 30000, // Refetch every 30 seconds
  });

  // Get notification service status
  const { data: serviceStatus } = useQuery({
    queryKey: ["/api/notifications/status"],
    refetchInterval: 60000, // Check status every minute
  });

  // Track location mutation
  const trackLocationMutation = useMutation({
    mutationFn: async ({ lat, lng }: { lat: number; lng: number }) => {
      return apiRequest(`/api/user/${userId}/track-location`, "POST", {
        latitude: lat,
        longitude: lng
      });
    },
    onSuccess: () => {
      // Refresh notifications after tracking location
      queryClient.invalidateQueries({ queryKey: ["/api/user", userId, "notifications"] });
    },
  });

  // Mark notification as read
  const markAsReadMutation = useMutation({
    mutationFn: async (notificationId: number) => {
      return apiRequest(`/api/notifications/${notificationId}/read`, "PATCH");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/user", userId, "notifications"] });
    },
  });

  // Test nearby notifications (for debugging)
  const testNearbyMutation = useMutation({
    mutationFn: async ({ lat, lng }: { lat: number; lng: number }) => {
      return apiRequest("/api/notifications/test-nearby", "POST", {
        userId,
        latitude: lat,
        longitude: lng
      });
    },
  });

  // Auto-track location when available and enabled
  useEffect(() => {
    if (isEnabled && latitude && longitude) {
      trackLocationMutation.mutate({ lat: latitude, lng: longitude });
    }
  }, [latitude, longitude, isEnabled]);

  // Get unread notifications count
  const unreadCount = notifications?.filter(n => !n.read).length || 0;

  // Get recent trending notifications
  const trendingNotifications = notifications?.filter(n => 
    n.type === "nearby_trending" && !n.read
  ) || [];

  return {
    notifications: notifications || [],
    unreadCount,
    trendingNotifications,
    isLoading,
    isEnabled,
    serviceStatus,
    setIsEnabled,
    markAsRead: (id: number) => markAsReadMutation.mutate(id),
    testNearby: (lat: number, lng: number) => testNearbyMutation.mutate({ lat, lng }),
    isTracking: trackLocationMutation.isPending,
    isMarkingRead: markAsReadMutation.isPending,
  };
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

