import { useState, useEffect, useCallback } from 'react';
import { usePushNotifications } from './usePushNotifications';
import { useToast } from './use-toast';
import { useGeolocation } from './use-geolocation';

interface LocationTrackingState {
  isTracking: boolean;
  position: GeolocationPosition | null;
  error: GeolocationPositionError | null;
  lastUpdate: Date | null;
}

export function useLocationTracking() {
  const [state, setState] = useState<LocationTrackingState>({
    isTracking: false,
    position: null,
    error: null,
    lastUpdate: null,
  });

  const { checkNearby } = usePushNotifications();
  const { toast } = useToast();

  const { latitude, longitude } = useGeolocation();

  const startTracking = useCallback(() => {
    if (!latitude || !longitude) {
      toast({
        title: "Location Required",
        description: "Please allow location access to enable tracking",
        variant: "destructive",
      });
      return;
    }

    setState(prev => ({ ...prev, isTracking: true, error: null }));

    // Use current position from useGeolocation hook
    checkNearby(latitude, longitude);

    toast({
      title: "Tracking Started",
      description: "You'll get alerts when trending spots are nearby",
      duration: 3000,
    });

    return () => {
      setState(prev => ({ ...prev, isTracking: false }));
    };
  }, [checkNearby, toast, latitude, longitude]);

  const stopTracking = useCallback(() => {
    setState(prev => ({ ...prev, isTracking: false }));
  }, []);

  const getCurrentPosition = useCallback(() => {
    if (latitude && longitude) {
      return Promise.resolve({
        latitude,
        longitude,
        accuracy: null
      });
    }
    return Promise.reject(new Error('Location not available'));
  }, [latitude, longitude]);

  // Auto-start tracking when location becomes available
  useEffect(() => {
    const shouldAutoTrack = localStorage.getItem('socialiser-auto-track') === 'true';
    if (shouldAutoTrack && !state.isTracking && latitude && longitude) {
      startTracking();
    }
  }, [startTracking, state.isTracking, latitude, longitude]);

  const enableAutoTracking = useCallback(() => {
    localStorage.setItem('socialiser-auto-track', 'true');
    startTracking();

    toast({
      title: "Auto-Tracking Enabled",
      description: "You'll get alerts when trending spots are nearby",
      duration: 3000,
    });
  }, [startTracking, toast]);

  const disableAutoTracking = useCallback(() => {
    localStorage.setItem('socialiser-auto-track', 'false');
    stopTracking();

    toast({
      title: "Auto-Tracking Disabled",
      description: "You can still manually check for nearby spots",
      duration: 3000,
    });
  }, [stopTracking, toast]);

  return {
    ...state,
    startTracking,
    stopTracking,
    getCurrentPosition,
    enableAutoTracking,
    disableAutoTracking,
    isAutoTrackingEnabled: localStorage.getItem('socialiser-auto-track') === 'true',
  };
}

client/src/hooks/useLocationWebSocket.ts.content:
import React, { useEffect, useRef, useState } from 'react';
import { useGeolocation } from './use-geolocation';
import { useToast } from '@/hooks/use-toast';

interface LocationWebSocketOptions {
  userId: string;
  onNearbySpots?: (spots: any[]) => void;
  onLocationUpdate?: (location: { lat: number; lng: number }) => void;
  enabled?: boolean;
}

export function useLocationWebSocket(options: LocationWebSocketOptions) {
  const { userId, onNearbySpots, onLocationUpdate, enabled = true } = options;
  const { latitude, longitude } = useGeolocation();
  const [isConnected, setIsConnected] = useState(false);
  const [nearbySpots, setNearbySpots] = useState<any[]>([]);
  const wsRef = useRef<WebSocket | null>(null);
  const locationUpdateIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const { toast } = useToast();

  // WebSocket connection with iOS-specific handling
  const connect = () => {
    // Disable WebSocket completely for iOS to prevent promise rejections
    const isIOSNative = (window as any).Capacitor?.isNativePlatform();
    if (isIOSNative) {
      console.log('[iOS] WebSocket disabled for iOS compatibility');
      return;
    }

    if (wsRef.current?.readyState === WebSocket.OPEN || wsRef.current?.readyState === WebSocket.CONNECTING) {
      return;
    }

    // Clean up existing connection first
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }

    try {
      // Detect if running in iOS app
      const isCapacitor = (window as any).Capacitor?.isNativePlatform();
      const wsUrl = isCapacitor 
        ? 'wss://hot-girl-hunt-fenelon141.replit.app/ws'
        : `${window.location.protocol === 'https:' ? 'wss:' : 'ws:'}//${window.location.host}/ws`;


      wsRef.current = new WebSocket(wsUrl);

      wsRef.current.onopen = () => {

        setIsConnected(true);

        // Authenticate immediately
        if (userId) {
          wsRef.current?.send(JSON.stringify({
            type: 'authenticate',
            userId: userId
          }));
        }

        // Start location tracking if we have coordinates
        if (latitude && longitude) {
          sendLocationUpdate(latitude, longitude);
          startLocationTracking();
        }
      };

      wsRef.current.onmessage = (event) => {
        try {
          const message = JSON.parse(event.data);


          switch (message.type) {
            case 'authenticated':

              break;

            case 'nearby_spots_update':

              setNearbySpots(message.spots || []);
              onNearbySpots?.(message.spots || []);
              break;

            case 'location_tracked':

              onLocationUpdate?.(message.location);
              break;

            case 'new_spot_nearby':
              toast({
                title: "New spot discovered! 🎯",
                description: `${message.spotName} is now nearby`,
              });
              break;

            case 'friend_nearby':
              toast({
                title: "Friend nearby! 👋",
                description: `${message.friendName} is close to you`,
              });
              break;
          }
        } catch (error) {
          // Message parsing error
        }
      };

      wsRef.current.onerror = (error) => {
        // Connection error - prevent unhandled rejections
        console.log('[iOS] WebSocket error:', error);
        setIsConnected(false);
        setConnectionStatus('error');
      };

      wsRef.current.onclose = (event) => {

        setIsConnected(false);

        // Prevent multiple reconnection attempts (disable for iOS to prevent errors)
        const isIOSNative = (window as any).Capacitor?.isNativePlatform();
        if (event.code !== 1000 && enabled && !wsRef.current && !isIOSNative) {
          setTimeout(() => {
            if (!wsRef.current || wsRef.current.readyState === WebSocket.CLOSED) {
              connect();
            }
          }, 5000);
        }
      };

    } catch (error) {
      // Failed to create connection - prevent unhandled rejections
      console.log('[iOS] WebSocket connection failed:', error);
      setIsConnected(false);
      setConnectionStatus('error');
    }
  };

  // Send location update to server
  const sendLocationUpdate = (lat: number, lng: number) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      const message = {
        type: 'location_update',
        userId: userId,
        latitude: lat,
        longitude: lng,
        timestamp: Date.now()
      };


      wsRef.current.send(JSON.stringify(message));
    }
  };

  // Request nearby spots for current location
  const requestNearbySpots = (lat?: number, lng?: number) => {
    const currentLat = lat || latitude;
    const currentLng = lng || longitude;

    if (wsRef.current?.readyState === WebSocket.OPEN && currentLat && currentLng) {
      const message = {
        type: 'request_nearby_spots',
        userId: userId,
        latitude: currentLat,
        longitude: currentLng,
        radius: 1500 // 1.5km radius
      };


      wsRef.current.send(JSON.stringify(message));
    }
  };

  // Start periodic location tracking
  const startLocationTracking = () => {
    if (locationUpdateIntervalRef.current) {
      clearInterval(locationUpdateIntervalRef.current);
    }

    // Send location updates every 30 seconds
    locationUpdateIntervalRef.current = setInterval(() => {
      if (latitude && longitude) {
        sendLocationUpdate(latitude, longitude);
        requestNearbySpots(latitude, longitude);
      }
    }, 30000);
  };

  // Stop location tracking
  const stopLocationTracking = () => {
    if (locationUpdateIntervalRef.current) {
      clearInterval(locationUpdateIntervalRef.current);
      locationUpdateIntervalRef.current = null;
    }
  };

  // Initialize connection when enabled
  useEffect(() => {
    if (enabled) {
      connect();
    }

    return () => {
      stopLocationTracking();
      if (wsRef.current) {
        wsRef.current.close(1000, 'Component unmounting');
      }
    };
  }, [enabled]);

  // Send location updates when coordinates change
  useEffect(() => {
    if (isConnected && latitude && longitude) {
      sendLocationUpdate(latitude, longitude);
      requestNearbySpots(latitude, longitude);
    }
  }, [isConnected, latitude, longitude]);

  return {
    isConnected,
    nearbySpots,
    sendLocationUpdate,
    requestNearbySpots,
    startLocationTracking,
    stopLocationTracking,
    connect,
    disconnect: () => {
      stopLocationTracking();
      wsRef.current?.close(1000, 'Manual disconnect');
    }
  };
}

client/src/hooks/useNativeStorage.ts.content:
import { useState } from 'react';
import { useToast } from '@/hooks/use-toast';

interface StoredStory {
  id: string;
  userId: string;
  imageUrl: string;
  caption: string;
  type: 'photo' | 'video';
  createdAt: Date;
  expiresAt: Date;
}

// Native iOS storage for stories without API dependencies
export function useNativeStorage() {
  const { toast } = useToast();

  // Save story locally to device storage
  const saveStoryLocally = async (storyData: Omit<StoredStory, 'id' | 'createdAt' | 'expiresAt'>): Promise<boolean> => {
    try {
      const story: StoredStory = {
        ...storyData,
        id: `story_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        createdAt: new Date(),
        expiresAt: new Date(Date.now() + 24 * 60 * 60 * 1000) // 24 hours
      };

      // Store in localStorage for iOS compatibility
      const existingStories = getLocalStories();
      const updatedStories = [...existingStories, story];

      localStorage.setItem('socialiser_stories', JSON.stringify(updatedStories));

      toast({
        title: "Story saved! ✨",
        description: "Your story is stored locally on your device",
      });

      return true;
    } catch (error) {
      console.error('Failed to save story locally:', error);
      toast({
        title: "Storage failed",
        description: "Couldn't save story to device storage",
        variant: "destructive",
      });
      return false;
    }
  };

  // Get all local stories
  const getLocalStories = (): StoredStory[] => {
    try {
      const stored = localStorage.getItem('socialiser_stories');
      if (!stored) return [];

      const stories: StoredStory[] = JSON.parse(stored);
      const now = new Date();

      // Filter out expired stories (older than 24 hours)
      const activeStories = stories.filter(story => new Date(story.expiresAt) > now);

      // Update storage with only active stories
      if (activeStories.length !== stories.length) {
        localStorage.setItem('socialiser_stories', JSON.stringify(activeStories));
      }

      return activeStories.sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
    } catch (error) {
      console.error('Failed to get local stories:', error);
      return [];
    }
  };

  // Delete a specific story
  const deleteLocalStory = async (storyId: string): Promise<boolean> => {
    try {
      const existingStories = getLocalStories();
      const updatedStories = existingStories.filter(story => story.id !== storyId);

      localStorage.setItem('socialiser_stories', JSON.stringify(updatedStories));

      toast({
        title: "Story deleted",
        description: "Story removed from your device",
      });

      return true;
    } catch (error) {
      console.error('Failed to delete story:', error);
      return false;
    }
  };

  // Clear all stories
  const clearAllStories = async (): Promise<boolean> => {
    try {
      localStorage.removeItem('socialiser_stories');
      toast({
        title: "All stories cleared",
        description: "Local story storage cleared",
      });
      return true;
    } catch (error) {
      console.error('Failed to clear stories:', error);
      return false;
    }
  };

  return {
    saveStoryLocally,
    getLocalStories,
    deleteLocalStory,
    clearAllStories
  };
}

client/src/hooks/useOfflineStorage.ts.content:
import { useState, useEffect } from 'react';

interface SavedSpot {
  id: string | number;
  name: string;
  description: string;
  latitude: number;
  longitude: number;
  rating: number;
  imageUrl?: string;
  category: string;
  savedAt: string;
  huntedOffline?: boolean;
}

interface OfflineAction {
  id: string;
  type: 'spot_hunt' | 'post_create' | 'friend_request' | 'squad_join';
  data: any;
  timestamp: string;
  synced: boolean;
}

export function useOfflineStorage() {
  const [savedSpots, setSavedSpots] = useState<SavedSpot[]>([]);
  const [pendingActions, setPendingActions] = useState<OfflineAction[]>([]);
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  // Load saved data from localStorage on mount
  useEffect(() => {
    const loadSavedData = () => {
      try {
        const spots = localStorage.getItem('savedSpots');
        if (spots) {
          setSavedSpots(JSON.parse(spots));
        }

        const actions = localStorage.getItem('pendingActions');
        if (actions) {
          setPendingActions(JSON.parse(actions));
        }
      } catch (error) {
        // Error loading offline data - continue with empty state
      }
    };

    loadSavedData();

    // Listen for online/offline events
    const handleOnline = () => {
      setIsOnline(true);
      syncPendingActions();
    };

    const handleOffline = () => {

      setIsOnline(false);
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  // Save spots to localStorage whenever savedSpots changes
  useEffect(() => {
    try {
      localStorage.setItem('savedSpots', JSON.stringify(savedSpots));
    } catch (error) {

    }
  }, [savedSpots]);

  // Save actions to localStorage whenever pendingActions changes
  useEffect(() => {
    try {
      localStorage.setItem('pendingActions', JSON.stringify(pendingActions));
    } catch (error) {
      // Error saving actions - continue silently
    }
  }, [pendingActions]);

  const saveSpot = (spot: Omit<SavedSpot, 'savedAt'>) => {
    const savedSpot: SavedSpot = {
      ...spot,
      savedAt: new Date().toISOString()
    };

    setSavedSpots(prev => {
      const exists = prev.find(s => s.id === spot.id);
      if (exists) {
        return prev; // Already saved
      }
      return [...prev, savedSpot];
    });

    return true;
  };

  const removeSavedSpot = (spotId: string | number) => {
    setSavedSpots(prev => prev.filter(spot => spot.id !== spotId));
  };

  const isSaved = (spotId: string | number) => {
    return savedSpots.some(spot => spot.id === spotId);
  };

  const addPendingAction = (action: Omit<OfflineAction, 'id' | 'timestamp' | 'synced'>) => {
    const pendingAction: OfflineAction = {
      ...action,
      id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date().toISOString(),
      synced: false
    };

    setPendingActions(prev => [...prev, pendingAction]);
    return pendingAction.id;
  };

  const markActionSynced = (actionId: string) => {
    setPendingActions(prev =>
      prev.map(action =>
        action.id === actionId ? { ...action, synced: true } : action
      )
    );
  };

  const removeSyncedActions = () => {
    setPendingActions(prev => prev.filter(action => !action.synced));
  };

  const syncPendingActions = async () => {
    if (!isOnline || pendingActions.length === 0) {
      return;
    }



    for (const action of pendingActions.filter(a => !a.synced)) {
      try {
        let success = false;

        switch (action.type) {
          case 'spot_hunt':
            success = await syncSpotHunt(action);
            break;
          case 'post_create':
            success = await syncPostCreate(action);
            break;
          case 'friend_request':
            success = await syncFriendRequest(action);
            break;
          case 'squad_join':
            success = await syncSquadJoin(action);
            break;
        }

        if (success) {
          markActionSynced(action.id);
        }
      } catch (error) {

      }
    }

    // Clean up synced actions after a delay
    setTimeout(removeSyncedActions, 5000);
  };

  const syncSpotHunt = async (action: OfflineAction): Promise<boolean> => {
    try {
      const response = await fetch(`/api/spots/${action.data.spotId}/hunt`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: action.data.userId,
          latitude: action.data.latitude,
          longitude: action.data.longitude
        })
      });
      return response.ok;
    } catch (error) {

      return false;
    }
  };

  const syncPostCreate = async (action: OfflineAction): Promise<boolean> => {
    try {
      const response = await fetch('/api/posts', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(action.data)
      });
      return response.ok;
    } catch (error) {

      return false;
    }
  };

  const syncFriendRequest = async (action: OfflineAction): Promise<boolean> => {
    try {
      const response = await fetch('/api/friend-requests', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(action.data)
      });
      return response.ok;
    } catch (error) {

      return false;
    }
  };

  const syncSquadJoin = async (action: OfflineAction): Promise<boolean> => {
    try {
      const response = await fetch(`/api/squads/${action.data.squadId}/join`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(action.data)
      });
      return response.ok;
    } catch (error) {

      return false;
    }
  };

  // Offline spot hunting
  const huntSpotOffline = (spot: SavedSpot, userId: string, latitude: number, longitude: number) => {
    // Mark spot as hunted offline
    setSavedSpots(prev =>
      prev.map(s =>
        s.id === spot.id ? { ...s, huntedOffline: true } : s
      )
    );

    // Add to pending actions for sync when online
    addPendingAction({
      type: 'spot_hunt',
      data: {
        spotId: spot.id,
        userId,
        latitude,
        longitude
      }
    });

    return {
      success: true,
      points: 50, // Default offline points
      message: 'Spot hunted offline! Will sync when you\'re back online.'
    };
  };

  const getOfflineCapabilities = () => {
    return {
      canHuntSpots: savedSpots.length > 0,
      canViewSavedSpots: true,
      canCreatePosts: true, // Can create offline, sync later
      savedSpotsCount: savedSpots.length,
      pendingActionsCount: pendingActions.filter(a => !a.synced).length
    };
  };

  return {
    savedSpots,
    pendingActions: pendingActions.filter(a => !a.synced),
    isOnline,
    saveSpot,
    removeSavedSpot,
    isSaved,
    huntSpotOffline,
    addPendingAction,
    syncPendingActions,
    getOfflineCapabilities
  };
}

