import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useToast } from '@/hooks/use-toast';
import { useWebSocket } from '@/hooks/useWebSocket';
import { apiRequest } from '@/lib/queryClient';

interface Notification {
  id: number;
  type: string;
  title: string;
  message: string;
  data?: any;
  read: boolean;
  createdAt: string;
}

export function usePushNotifications() {
  const [isTracking, setIsTracking] = useState(false);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // WebSocket integration for real-time notifications
  const { isConnected } = useWebSocket({
    userId: "1", // Guest user
    onMessage: (message) => {
      if (message.type === 'trending_spot_notification') {
        const notification = message.notification;

        // Show immediate toast notification
        toast({
          title: notification.title,
          description: notification.message,
          duration: 5000,
        });

        // Refresh notifications list
        queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
      }
    }
  });

  // Get notification service status
  const { data: serviceStatus } = useQuery({
    queryKey: ['/api/notifications/status'],
    refetchInterval: 30000, // Check every 30 seconds
  });

  // Get user notifications
  const { data: notifications, isLoading: notificationsLoading } = useQuery<Notification[]>({
    queryKey: ['/api/notifications'],
    refetchInterval: 60000, // Refresh every minute
  });

  // Test nearby spots mutation
  const testNearbyMutation = useMutation({
    mutationFn: async ({ latitude, longitude }: { latitude: number; longitude: number }) => {
      return await apiRequest('/api/test-nearby', 'POST', { latitude, longitude });
    },
    onMutate: () => {
      setIsTracking(true);
    },
    onSuccess: (data: any) => {


      if (data.newNotifications > 0) {
        toast({
          title: "New Trending Spots Found! 🔥",
          description: `${data.newNotifications} trendy spots discovered nearby`,
          duration: 4000,
        });
      } else {
        toast({
          title: "Location Tracked ✅",
          description: "No new trending spots found right now, but we're watching!",
          duration: 3000,
        });
      }

      // Refresh notifications
      queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
      queryClient.invalidateQueries({ queryKey: ['/api/notifications/status'] });
    },
    onError: (error) => {

      toast({
        title: "Error",
        description: "Failed to check nearby trending spots",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsTracking(false);
    }
  });

  // Mark notification as read mutation
  const markReadMutation = useMutation({
    mutationFn: async (notificationId: number) => {
      return await apiRequest(`/api/notifications/${notificationId}/read`, 'POST');
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
    }
  });

  // Check for nearby trending spots
  const checkNearbyMutation = useMutation({
    mutationFn: async ({ latitude, longitude }: { latitude: number; longitude: number }) => {
      return await apiRequest('/api/notifications/test-nearby', 'POST', { 
        userId: "1", 
        latitude, 
        longitude 
      });
    },
    onSuccess: (data: any) => {
      if (data.newNotifications > 0) {
        toast({
          title: "Trending Spots Nearby! 🔥",
          description: `Found ${data.newNotifications} trendy spots in your area`,
          duration: 5000,
        });

        // Refresh notifications
        queryClient.invalidateQueries({ queryKey: ['/api/notifications'] });
      }
    }
  });

  // Get unread notifications count
  const unreadCount = notifications?.filter(n => !n.read).length || 0;

  // Get recent trending notifications
  const trendingNotifications = notifications?.filter(n => 
    n.type === 'nearby_trending' && 
    new Date(n.createdAt) > new Date(Date.now() - 24 * 60 * 60 * 1000) // Last 24 hours
  ) || [];

  const testNearby = (latitude: number, longitude: number) => {
    testNearbyMutation.mutate({ latitude, longitude });
  };

  const checkNearby = (latitude: number, longitude: number) => {
    checkNearbyMutation.mutate({ latitude, longitude });
  };

  const markAsRead = (notificationId: number) => {
    markReadMutation.mutate(notificationId);
  };

  return {
    notifications,
    notificationsLoading,
    unreadCount,
    trendingNotifications,
    serviceStatus,
    isTracking,
    isConnected,
    testNearby,
    checkNearby,
    markAsRead
  };
}

client/src/hooks/useStories.ts.content:
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
import { useNativeStorage } from './useNativeStorage';

// Check if we're in iOS native app
const isIOSNative = typeof window !== 'undefined' && (window as any).Capacitor;

export interface Story {
  id: number;
  userId: string;
  imageUrl: string;
  caption: string;
  type: 'photo' | 'video';
  createdAt: string;
  expiresAt: string;
  views: number;
  user: {
    id: string;
    username: string;
    avatar: string;
  };
}

export function useStories() {
  const { getLocalStories } = useNativeStorage();

  return useQuery<Story[]>({
    queryKey: ['/api/stories'],
    queryFn: async () => {
      if (isIOSNative) {
        // Use local storage for iOS to avoid network issues
        const localStories = getLocalStories();
        return localStories.map(story => ({
          ...story,
          id: story.id as any, // Convert string to number for compatibility
          views: 0,
          user: {
            id: story.userId,
            username: 'You',
            avatar: '/placeholder-avatar.png'
          }
        }));
      } else {
        // Use API for web
        const response = await apiRequest('GET', '/api/stories');
        return response;
      }
    },
    retry: false,
  });
}

export function useCreateStory() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { saveStoryLocally } = useNativeStorage();

  return useMutation({
    mutationFn: async (storyData: {
      userId: string;
      imageUrl: string;
      caption: string;
      type: 'photo' | 'video';
      expiresAt: Date;
    }) => {

      if (isIOSNative) {
        // Native iOS: Store locally without API calls
        console.log('iOS Native: Saving story locally');
        const success = await saveStoryLocally({
          userId: storyData.userId,
          imageUrl: storyData.imageUrl, // Keep base64 data URL as-is
          caption: storyData.caption,
          type: storyData.type
        });

        if (!success) {
          throw new Error('Failed to save story locally');
        }

        return { success: true, message: 'Story saved locally' };
      } else {
        // Web: Use API with image upload
        console.log('Web: Using API for story upload');

        // If imageUrl is base64, upload to object storage first
        let finalImageUrl = storyData.imageUrl;

        if (storyData.imageUrl.startsWith('data:')) {
          const uploadResponse = await apiRequest('POST', '/upload-story-image', {
            dataUrl: storyData.imageUrl,
            userId: storyData.userId
          });

          if (uploadResponse.success && uploadResponse.imageUrl) {
            finalImageUrl = uploadResponse.imageUrl;
          } else {
            throw new Error('Failed to upload image to object storage');
          }
        }

        const finalStoryData = {
          ...storyData,
          imageUrl: finalImageUrl
        };

        const response = await apiRequest('POST', '/api/stories', finalStoryData);
        return response;
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/stories'] });
      if (isIOSNative) {
        toast({
          title: "Story saved! ✨",
          description: "Your story is stored locally on your device",
        });
      } else {
        toast({
          title: "Story posted! ✨",
          description: "Your story is active for 24 hours!",
        });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Story failed",
        description: error.message || "Couldn't save your story. Try again!",
        variant: "destructive",
      });
    },
  });
}

