import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import BottomNavigation from "@/components/bottom-navigation";
import { ArrowLeft, Trophy, Lock } from "lucide-react";
import { Link } from "wouter";
import type { Badge as BadgeType, UserBadge } from "@shared/schema";

export default function Badges() {
  const { data: allBadges, isLoading: allBadgesLoading } = useQuery<BadgeType[]>({
    queryKey: ["/api/badges"]
  });

  const { data: userBadges, isLoading: userBadgesLoading } = useQuery<(UserBadge & { badge: BadgeType })[]>({
    queryKey: ["/api/user/1/badges"]
  });

  const earnedBadgeIds = new Set(userBadges?.map(ub => ub.badgeId) || []);

  const getRarityColor = (rarity: string) => {
    switch (rarity) {
      case 'common': return 'bg-gray-100 text-gray-600';
      case 'rare': return 'bg-blue-100 text-blue-600';
      case 'epic': return 'bg-purple-100 text-purple-600';
      case 'legendary': return 'bg-yellow-100 text-yellow-600';
      default: return 'bg-gray-100 text-gray-600';
    }
  };

  const getRarityGlow = (rarity: string) => {
    switch (rarity) {
      case 'common': return '';
      case 'rare': return 'shadow-blue-200';
      case 'epic': return 'shadow-purple-200';
      case 'legendary': return 'shadow-yellow-200 glow-effect';
      default: return '';
    }
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Header */}
      <div className="bg-gradient-to-r from-pink-400 to-purple-300 p-4 text-white">
        <div className="flex items-center space-x-3">
          <Link href="/" asChild>
            <Button variant="ghost" size="sm" className="text-white hover:bg-white/20">
              <ArrowLeft className="w-4 h-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-xl font-bold">Badge Collection 🏆</h1>
            <p className="text-xs opacity-90">
              {userBadges?.length || 0} of {allBadges?.length || 0} badges earned ✨
            </p>
          </div>
        </div>
      </div>

      {/* Stats */}
      <div className="p-4 bg-gradient-to-r from-purple-50 to-pink-50">
        <div className="grid grid-cols-3 gap-4">
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-800">{userBadges?.length || 0}</div>
            <div className="text-xs text-gray-600">Earned</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-800">
              {userBadges?.filter(ub => ub.badge.rarity === 'rare' || ub.badge.rarity === 'epic').length || 0}
            </div>
            <div className="text-xs text-gray-600">Rare+</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-gray-800">
              {Math.round(((userBadges?.length || 0) / (allBadges?.length || 1)) * 100)}%
            </div>
            <div className="text-xs text-gray-600">Complete</div>
          </div>
        </div>
      </div>

      {/* Badge Categories */}
      <div className="p-4 space-y-4 pb-20">
        {/* Recently Earned */}
        <div>
          <h2 className="text-lg font-bold text-gray-800 mb-3">Recently Earned 🎉</h2>
          <div className="grid grid-cols-2 gap-3">
            {userBadgesLoading ? (
              [1, 2, 3, 4].map(i => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-4 text-center">
                    <div className="w-12 h-12 bg-gray-200 rounded-full mx-auto mb-2"></div>
                    <div className="h-4 bg-gray-200 rounded mb-2"></div>
                    <div className="h-3 bg-gray-200 rounded"></div>
                  </CardContent>
                </Card>
              ))
            ) : (
              userBadges?.slice(-4).map(userBadge => (
                <Card key={userBadge.id} className={`border-2 border-green-200 ${getRarityGlow(userBadge.badge.rarity)}`}>
                  <CardContent className="p-4 text-center">
                    <div className="text-3xl mb-2">{userBadge.badge.emoji}</div>
                    <h3 className="font-semibold text-sm text-gray-800">{userBadge.badge.name}</h3>
                    <p className="text-xs text-gray-600 mb-2">{userBadge.badge.description}</p>
                    <Badge className={getRarityColor(userBadge.badge.rarity)}>
                      {userBadge.badge.rarity}
                    </Badge>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </div>

        {/* All Badges */}
        <div>
          <h2 className="text-lg font-bold text-gray-800 mb-3">All Badges 📋</h2>
          <div className="grid grid-cols-2 gap-3">
            {allBadgesLoading ? (
              [1, 2, 3, 4, 5, 6].map(i => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-4 text-center">
                    <div className="w-12 h-12 bg-gray-200 rounded-full mx-auto mb-2"></div>
                    <div className="h-4 bg-gray-200 rounded mb-2"></div>
                    <div className="h-3 bg-gray-200 rounded"></div>
                  </CardContent>
                </Card>
              ))
            ) : (
              allBadges?.map(badge => {
                const isEarned = earnedBadgeIds.has(badge.id);
                return (
                  <Card 
                    key={badge.id} 
                    className={`${isEarned 
                      ? `border-2 border-green-200 ${getRarityGlow(badge.rarity)}` 
                      : 'border border-gray-200 opacity-60'
                    }`}
                  >
                    <CardContent className="p-4 text-center relative">
                      {!isEarned && (
                        <div className="absolute inset-0 bg-gray-50 bg-opacity-80 rounded-lg flex items-center justify-center">
                          <Lock className="w-6 h-6 text-gray-400" />
                        </div>
                      )}
                      <div className="text-3xl mb-2">{badge.emoji}</div>
                      <h3 className="font-semibold text-sm text-gray-800">{badge.name}</h3>
                      <p className="text-xs text-gray-600 mb-2">{badge.description}</p>
                      <Badge className={getRarityColor(badge.rarity)}>
                        {badge.rarity}
                      </Badge>
                    </CardContent>
                  </Card>
                );
              })
            )}
          </div>
        </div>
      </div>

      <BottomNavigation />
    </div>
  );
}

client/src/pages/discover-feed.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import BottomNavigation from "@/components/bottom-navigation";
import TopNavigation from "@/components/top-navigation";
import { 
  StoriesStrip, 
  VerticalFeed, 
  ExploreGrid, 
  UpdatesFeed, 
  DoubleTapLike,
  PullToRefreshContainer 
} from "@/components/social-media-patterns";
import { Heart, MessageCircle, Share, Bookmark, Play, Sparkles, TrendingUp, MapPin } from "lucide-react";
import type { Spot } from "@shared/schema";

export default function DiscoverFeed() {
  const [feedType, setFeedType] = useState<"cards" | "vertical" | "grid">("cards");
  const [likedPosts, setLikedPosts] = useState<Set<number>>(new Set());

  const { data: spots = [], refetch, isLoading } = useQuery<Spot[]>({
    queryKey: ["/api/spots/trending"],
    retry: false
  });

  const handleLike = (spotId: number) => {
    setLikedPosts(prev => {
      const newSet = new Set(prev);
      if (newSet.has(spotId)) {
        newSet.delete(spotId);
      } else {
        newSet.add(spotId);
      }
      return newSet;
    });
  };

  const handleRefresh = () => {
    refetch();
  };

  const renderCard = (spot: Spot) => (
    <DoubleTapLike key={spot.id} onLike={() => handleLike(spot.id)}>
      <Card className="overflow-hidden shadow-lg border-0 bg-white mb-4">
        <CardContent className="p-0">
          {/* Header with spot info */}
          <div className="flex items-center justify-between p-4 pb-2">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-gradient-to-br from-pink-400 to-purple-400 rounded-full flex items-center justify-center">
                <MapPin className="w-5 h-5 text-white" />
              </div>
              <div>
                <div className="font-semibold text-sm">📍 {spot.address?.split(',')[0]}</div>
                <div className="text-xs text-gray-500">Trending spot</div>
              </div>
            </div>
          </div>

          {/* Image */}
          <div className="relative">
            <img
              src={spot.imageUrl || `https://images.unsplash.com/photo-1554118811-1e0d58224f24?w=400&h=300&fit=crop`}
              alt={spot.name}
              className="w-full h-64 object-cover"
            />
            <div className="absolute top-4 left-4 bg-black/50 backdrop-blur-sm text-white px-3 py-1 rounded-full text-sm font-medium">
              ⭐ {spot.rating}
            </div>
          </div>

          {/* Actions */}
          <div className="p-4">
            <div className="flex items-center justify-between mb-3">
              <div className="flex items-center space-x-4">
                <Button
                  variant="ghost"
                  size="sm"
                  className="p-0 h-auto"
                  onClick={() => handleLike(spot.id)}
                >
                  <Heart className={`w-6 h-6 ${likedPosts.has(spot.id) ? 'fill-red-500 text-red-500' : 'text-gray-700'}`} />
                </Button>
                <Button variant="ghost" size="sm" className="p-0 h-auto">
                  <MessageCircle className="w-6 h-6 text-gray-700" />
                </Button>
                <Button variant="ghost" size="sm" className="p-0 h-auto">
                  <Share className="w-6 h-6 text-gray-700" />
                </Button>
              </div>
              <Button variant="ghost" size="sm" className="p-0 h-auto">
                <Bookmark className="w-6 h-6 text-gray-700" />
              </Button>
            </div>

            {/* Content */}
            <div className="space-y-2">
              <div className="text-lg font-semibold">{spot.name}</div>
              <div className="text-sm text-gray-600">{spot.description}</div>
              <div className="text-sm text-gray-500 capitalize">Category: {spot.category}</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </DoubleTapLike>
  );

  return (
    <div className="min-h-screen bg-gray-50">
      {feedType !== "vertical" && <TopNavigation />}

      {feedType === "vertical" ? (
        <div className="relative h-screen">
          {/* Back button for vertical feed */}
          <div className="absolute top-4 left-4 z-20">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => setFeedType("cards")}
              className="bg-black/50 backdrop-blur-sm text-white rounded-full p-2 hover:bg-black/70"
            >
              ←
            </Button>
          </div>
          <VerticalFeed spots={spots} />
        </div>
      ) : (
        <PullToRefreshContainer onRefresh={handleRefresh}>
          <div className="pb-24">
            {/* Stories Section */}
            {/* <div className="bg-white border-b border-gray-100">
              <StoriesStrip />
            </div> Temporarily disabled for App Store submission */}

            <Tabs value={feedType} onValueChange={(value) => setFeedType(value as any)} className="w-full">
              {/* Tab Navigation */}
              <div className="bg-white border-b border-gray-100 sticky top-0 z-10">
                <TabsList className="grid w-full grid-cols-3 bg-transparent h-12">
                  <TabsTrigger value="cards" className="data-[state=active]:bg-purple-100 data-[state=active]:text-purple-600">
                    Feed
                  </TabsTrigger>
                  <TabsTrigger value="vertical" className="data-[state=active]:bg-purple-100 data-[state=active]:text-purple-600">
                    Stories
                  </TabsTrigger>
                  <TabsTrigger value="grid" className="data-[state=active]:bg-purple-100 data-[state=active]:text-purple-600">
                    Explore
                  </TabsTrigger>
                </TabsList>
              </div>

              {/* Tab Content */}
              <TabsContent value="cards" className="mt-0">
                <div className="p-4 space-y-4">
                  {/* Recent Updates */}
                  <UpdatesFeed />

                  {/* Main Feed */}
                  {isLoading ? (
                    <div className="text-center py-8">
                      <div className="w-8 h-8 mx-auto mb-4 border-4 border-pink-300 border-t-pink-600 rounded-full animate-spin"></div>
                      <p className="text-gray-500">Loading trending spots...</p>
                    </div>
                  ) : spots.length === 0 ? (
                    <div className="text-center py-16">
                      <MapPin className="w-16 h-16 mx-auto text-gray-300 mb-4" />
                      <h3 className="text-lg font-semibold text-gray-800 mb-2">No trending spots</h3>
                      <p className="text-gray-500 mb-4">Check back soon for new discoveries</p>
                    </div>
                  ) : (
                    spots.map(renderCard)
                  )}
                </div>
              </TabsContent>

              <TabsContent value="vertical" className="mt-0">
                {/* This will trigger the full-screen vertical feed */}
                <div className="h-40 flex items-center justify-center">
                  {/* <Button 
                    onClick={() => setFeedType("vertical")}
                    className="bg-gradient-to-r from-purple-600 to-pink-600 text-white"
                  >
                    Enter Full Screen Stories
                  </Button> Temporarily disabled for App Store submission */}
                  <div className="text-center py-8">
                    <p className="text-gray-500">Stories feature coming soon</p>
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="grid" className="mt-0">
                <ExploreGrid>
                  {spots.length === 0 ? (
                    <div className="col-span-2 text-center py-16">
                      <Sparkles className="w-16 h-16 mx-auto text-gray-300 mb-4" />
                      <h3 className="text-lg font-semibold text-gray-800 mb-2">Explore coming soon</h3>
                      <p className="text-gray-500">Discover new spots in grid view</p>
                    </div>
                  ) : (
                    spots.map((spot) => (
                      <Card key={spot.id} className="overflow-hidden">
                        <div className="aspect-square relative">
                          <img src={spot.imageUrl} alt={spot.name} className="w-full h-full object-cover" />
                        </div>
                        <div className="p-2">
                          <h3 className="font-semibold text-sm truncate">{spot.name}</h3>
                        </div>
                      </Card>
                    ))
                  )}
                </ExploreGrid>
              </TabsContent>
            </Tabs>
          </div>
        </PullToRefreshContainer>
      )}

      {feedType !== "vertical" && <BottomNavigation />}
    </div>
  );
}

