import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Crown, Trophy, Users, MapPin, Plus, Target, Medal } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import TopNavigation from "@/components/top-navigation";
import BottomNavigation from "@/components/bottom-navigation";

interface Squad {
  id: number;
  name: string;
  description: string;
  city: string;
  country: string;
  totalPoints: number;
  totalSpotsHunted: number;
  isPublic: boolean;
  createdBy: string;
  createdAt: string;
}

interface SquadMember {
  id: number;
  squadId: number;
  userId: string;
  role: 'admin' | 'member';
  joinedAt: string;
  user: {
    id: number;
    username: string;
    avatar: string;
    level: number;
    totalPoints: number;
  };
}

interface GroupChallenge {
  id: number;
  title: string;
  description: string;
  challengeType: 'spot_hunting' | 'points_collection' | 'badge_collection' | 'social_activity';
  targetValue: number;
  timeLimit: number;
  city?: string;
  country?: string;
  isActive: boolean;
  startDate: string;
  endDate: string;
  createdAt: string;
}

export default function Squads() {
  // Using guest user for now - auth system will be added later
  const user = { id: 1, username: "Guest User" };
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [createSquadOpen, setCreateSquadOpen] = useState(false);
  const [createChallengeOpen, setCreateChallengeOpen] = useState(false);
  const [newSquad, setNewSquad] = useState({
    name: '',
    description: '',
    city: '',
    country: '',
    isPublic: true
  });
  const [newChallenge, setNewChallenge] = useState({
    title: '',
    description: '',
    challengeType: 'spot_hunting' as const,
    targetValue: 10,
    timeLimit: 7,
    city: '',
    country: ''
  });

  // Query user's squads
  const { data: userSquads = [], isLoading: squadsLoading } = useQuery({
    queryKey: [`/api/user/${user?.id}/squads`],
    enabled: !!user
  });

  // Query active group challenges
  const { data: groupChallenges = [], isLoading: challengesLoading } = useQuery({
    queryKey: ['/api/group-challenges'],
  });

  // Create squad mutation
  const createSquadMutation = useMutation({
    mutationFn: async (squadData: typeof newSquad) => {
      return apiRequest(`/api/squads`, {
        method: 'POST',
        body: { ...squadData, createdBy: user?.id.toString() }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: [`/api/user/${user?.id}/squads`] });
      setCreateSquadOpen(false);
      setNewSquad({ name: '', description: '', city: '', country: '', isPublic: true });
      toast({
        title: "Squad Created!",
        description: "Your squad is ready for action",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create squad",
        variant: "destructive",
      });
    },
  });

  // Create challenge mutation
  const createChallengeMutation = useMutation({
    mutationFn: async (challengeData: typeof newChallenge) => {
      const startDate = new Date();
      const endDate = new Date();
      endDate.setDate(endDate.getDate() + challengeData.timeLimit);

      return apiRequest(`/api/group-challenges`, {
        method: 'POST',
        body: {
          ...challengeData,
          startDate: startDate.toISOString(),
          endDate: endDate.toISOString(),
          isActive: true,
          createdBy: user?.id.toString()
        }
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/group-challenges'] });
      setCreateChallengeOpen(false);
      setNewChallenge({
        title: '',
        description: '',
        challengeType: 'spot_hunting',
        targetValue: 10,
        timeLimit: 7,
        city: '',
        country: ''
      });
      toast({
        title: "Challenge Created!",
        description: "Get ready to compete!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create challenge",
        variant: "destructive",
      });
    },
  });

  const handleCreateSquad = () => {
    if (!newSquad.name.trim()) {
      toast({
        title: "Name Required",
        description: "Please enter a squad name",
        variant: "destructive",
      });
      return;
    }
    createSquadMutation.mutate(newSquad);
  };

  const handleCreateChallenge = () => {
    if (!newChallenge.title.trim()) {
      toast({
        title: "Title Required", 
        description: "Please enter a challenge title",
        variant: "destructive",
      });
      return;
    }
    createChallengeMutation.mutate(newChallenge);
  };

  if (squadsLoading || challengesLoading) {
    return (
      <div className="container mx-auto p-4 space-y-6">
        <div className="h-8 bg-gray-200 rounded animate-pulse" />
        <div className="grid gap-4">
          {[1, 2, 3].map(i => (
            <div key={i} className="h-32 bg-gray-200 rounded animate-pulse" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="gradient-bg min-h-screen">
      <TopNavigation />
      <div className="container mx-auto p-4 space-y-6 pb-20">
      {/* Header */}
      <div className="text-center space-y-2">
        <h1 className="text-3xl font-bold bg-gradient-to-r from-pink-500 to-purple-600 bg-clip-text text-transparent">
          Squad Competition
        </h1>
        <p className="text-gray-600">Team up with friends and compete in city challenges</p>
      </div>

      {/* My Squads Section */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2">
              <Users className="w-5 h-5" />
              My Squads
            </CardTitle>
            <CardDescription>Your squad teams and stats</CardDescription>
          </div>
          <Dialog open={createSquadOpen} onOpenChange={setCreateSquadOpen}>
            <DialogTrigger asChild>
              <Button size="sm" className="bg-gradient-to-r from-pink-500 to-purple-600">
                <Plus className="w-4 h-4 mr-2" />
                Create Squad
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create New Squad</DialogTitle>
                <DialogDescription>
                  Start a squad and invite friends to compete together
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="squadName">Squad Name</Label>
                  <Input
                    id="squadName"
                    placeholder="The Pink Panthers"
                    value={newSquad.name}
                    onChange={(e) => setNewSquad({ ...newSquad, name: e.target.value })}
                  />
                </div>
                <div>
                  <Label htmlFor="squadDescription">Description</Label>
                  <Textarea
                    id="squadDescription"
                    placeholder="A fierce team of spot hunters..."
                    value={newSquad.description}
                    onChange={(e) => setNewSquad({ ...newSquad, description: e.target.value })}
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="city">City</Label>
                    <Input
                      id="city"
                      placeholder="London"
                      value={newSquad.city}
                      onChange={(e) => setNewSquad({ ...newSquad, city: e.target.value })}
                    />
                  </div>
                  <div>
                    <Label htmlFor="country">Country</Label>
                    <Input
                      id="country"
                      placeholder="UK"
                      value={newSquad.country}
                      onChange={(e) => setNewSquad({ ...newSquad, country: e.target.value })}
                    />
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <Switch
                    id="public"
                    checked={newSquad.isPublic}
                    onCheckedChange={(checked) => setNewSquad({ ...newSquad, isPublic: checked })}
                  />
                  <Label htmlFor="public">Public Squad (others can join)</Label>
                </div>
              </div>
              <DialogFooter>
                <Button 
                  onClick={handleCreateSquad}
                  disabled={createSquadMutation.isPending}
                  className="bg-gradient-to-r from-pink-500 to-purple-600"
                >
                  {createSquadMutation.isPending ? "Creating..." : "Create Squad"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </CardHeader>
        <CardContent>
          {userSquads.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Users className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>You're not in any squads yet.</p>
              <p className="text-sm">Create or join a squad to start competing!</p>
            </div>
          ) : (
            <div className="space-y-4">
              {userSquads.map((squadMember: any) => (
                <Card key={squadMember.id} className="border-pink-200">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-3">
                        <div className="w-12 h-12 bg-gradient-to-r from-pink-500 to-purple-600 rounded-full flex items-center justify-center text-white font-bold">
                          {squadMember.squad.name.charAt(0)}
                        </div>
                        <div>
                          <h3 className="font-semibold flex items-center gap-2">
                            {squadMember.squad.name}
                            {squadMember.role === 'admin' && (
                              <Crown className="w-4 h-4 text-yellow-500" />
                            )}
                          </h3>
                          <p className="text-sm text-gray-600 flex items-center gap-1">
                            <MapPin className="w-3 h-3" />
                            {squadMember.squad.city}, {squadMember.squad.country}
                          </p>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="flex items-center gap-2 text-sm">
                          <Trophy className="w-4 h-4 text-yellow-500" />
                          {squadMember.squad.totalPoints} pts
                        </div>
                        <div className="text-xs text-gray-500">
                          {squadMember.squad.totalSpotsHunted} spots hunted
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Active Challenges Section */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2">
              <Target className="w-5 h-5" />
              Group Challenges
            </CardTitle>
            <CardDescription>Compete with other squads and individuals</CardDescription>
          </div>
          <Dialog open={createChallengeOpen} onOpenChange={setCreateChallengeOpen}>
            <DialogTrigger asChild>
              <Button size="sm" variant="outline">
                <Plus className="w-4 h-4 mr-2" />
                Create Challenge
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create Group Challenge</DialogTitle>
                <DialogDescription>
                  Start a new challenge for squads and individuals to compete
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="challengeTitle">Challenge Title</Label>
                  <Input
                    id="challengeTitle"
                    placeholder="Weekend Spot Hunt"
                    value={newChallenge.title}
                    onChange={(e) => setNewChallenge({ ...newChallenge, title: e.target.value })}
                  />
                </div>
                <div>
                  <Label htmlFor="challengeDescription">Description</Label>
                  <Textarea
                    id="challengeDescription"
                    placeholder="Hunt the most aesthetic spots this weekend..."
                    value={newChallenge.description}
                    onChange={(e) => setNewChallenge({ ...newChallenge, description: e.target.value })}
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="targetValue">Target (spots to hunt)</Label>
                    <Input
                      id="targetValue"
                      type="number"
                      min="1"
                      value={newChallenge.targetValue}
                      onChange={(e) => setNewChallenge({ ...newChallenge, targetValue: parseInt(e.target.value) || 1 })}
                    />
                  </div>
                  <div>
                    <Label htmlFor="timeLimit">Days to Complete</Label>
                    <Input
                      id="timeLimit"
                      type="number"
                      min="1"
                      max="30"
                      value={newChallenge.timeLimit}
                      onChange={(e) => setNewChallenge({ ...newChallenge, timeLimit: parseInt(e.target.value) || 1 })}
                    />
                  </div>
                </div>
              </div>
              <DialogFooter>
                <Button 
                  onClick={handleCreateChallenge}
                  disabled={createChallengeMutation.isPending}
                  className="bg-gradient-to-r from-pink-500 to-purple-600"
                >
                  {createChallengeMutation.isPending ? "Creating..." : "Create Challenge"}
                </Button>
              </DialogFooter>
            </DialogContent>
          </Dialog>
        </CardHeader>
        <CardContent>
          {groupChallenges.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Target className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>No active challenges right now.</p>
              <p className="text-sm">Create a challenge to get the competition started!</p>
            </div>
          ) : (
            <div className="space-y-4">
              {groupChallenges.map((challenge: GroupChallenge) => (
                <Card key={challenge.id} className="border-purple-200">
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <div>
                        <h3 className="font-semibold">{challenge.title}</h3>
                        <p className="text-sm text-gray-600">{challenge.description}</p>
                        <div className="flex items-center gap-4 mt-2 text-xs text-gray-500">
                          <span>Target: {challenge.targetValue} spots</span>
                          <span>Ends: {new Date(challenge.endDate).toLocaleDateString()}</span>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge variant="outline" className="bg-purple-50">
                          <Medal className="w-3 h-3 mr-1" />
                          Active
                        </Badge>
                        <Button size="sm" variant="outline">
                          Join Challenge
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>
      </div>
      <BottomNavigation />
    </div>
  );
}

client/src/pages/taste-makers.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarImage, AvatarFallback } from "@/components/ui/avatar";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import BottomNavigation from "@/components/bottom-navigation";
import TopNavigation from "@/components/top-navigation";
import { ArrowLeft, Crown, Star, TrendingUp, MapPin, Instagram, Heart, Eye, Users, Sparkles, CheckCircle } from "lucide-react";
import { Link } from "wouter";
import type { User, Spot } from "@shared/schema";

interface TasteMakerWithUser {
  id: number;
  userId: string;
  tier: string;
  influenceScore: number;
  totalFollowers: number;
  spotsDiscovered: number;
  trendsStarted: number;
  verificationStatus: string;
  bio: string;
  specialties: string[];
  locationCity: string;
  locationCountry: string;
  instagramHandle?: string;
  tiktokHandle?: string;
  createdAt: Date;
  updatedAt: Date;
  user: User & { profilePictureUrl?: string };
  followersCount?: number;
  isFollowing?: boolean;
  recentEndorsements?: Array<{
    spot: Spot;
    caption: string;
    engagement: number;
    createdAt: string;
  }>;
}

export default function TasteMakers() {
  const [selectedTier, setSelectedTier] = useState<string>("all");
  const [followingIds, setFollowingIds] = useState<Set<number>>(new Set());

  // Fetch real taste makers data from API
  const { data: tasteMakers = [], isLoading } = useQuery<TasteMakerWithUser[]>({
    queryKey: ["/api/taste-makers", selectedTier],
    retry: false
  });

  const getTierColor = (tier: string) => {
    switch (tier) {
      case 'legendary': return 'bg-gradient-to-r from-yellow-400 to-orange-400 text-white';
      case 'elite': return 'bg-gradient-to-r from-purple-500 to-pink-500 text-white';
      case 'established': return 'bg-gradient-to-r from-blue-500 to-purple-500 text-white';
      case 'rising': return 'bg-gradient-to-r from-green-400 to-blue-400 text-white';
      default: return 'bg-gray-100 text-gray-600';
    }
  };

  const getTierIcon = (tier: string) => {
    switch (tier) {
      case 'legendary': return <Crown className="w-4 h-4" />;
      case 'elite': return <Star className="w-4 h-4" />;
      case 'established': return <TrendingUp className="w-4 h-4" />;
      case 'rising': return <Sparkles className="w-4 h-4" />;
      default: return null;
    }
  };

  const handleFollow = (tasteMakerId: number) => {
    setFollowingIds(prev => {
      const newSet = new Set(prev);
      if (newSet.has(tasteMakerId)) {
        newSet.delete(tasteMakerId);
      } else {
        newSet.add(tasteMakerId);
      }
      return newSet;
    });
  };

  const filteredTasteMakers = tasteMakers;

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-purple-50 to-white">
      <TopNavigation />

      {/* Header */}
      <div className="bg-gradient-to-r from-pink-500 via-purple-500 to-indigo-500 p-6 text-white">
        <div className="flex items-center space-x-3 mb-4">
          <Link href="/" asChild>
            <Button variant="ghost" size="sm" className="text-white hover:bg-white/20">
              <ArrowLeft className="w-4 h-4" />
            </Button>
          </Link>
          <div>
            <h1 className="text-2xl font-bold flex items-center space-x-2">
              <Crown className="w-6 h-6" />
              <span>Taste Makers</span>
            </h1>
            <p className="text-sm opacity-90">
              Follow the most influential spot hunters and discover trending places
            </p>
          </div>
        </div>

        {/* Tier Filter Tabs */}
        <div className="flex space-x-2 overflow-x-auto pb-2">
          {['all', 'legendary', 'elite', 'established', 'rising'].map((tier) => (
            <Button
              key={tier}
              variant={selectedTier === tier ? "secondary" : "ghost"}
              size="sm"
              onClick={() => setSelectedTier(tier)}
              className={`flex-shrink-0 ${selectedTier === tier ? 'bg-white text-purple-600' : 'text-white hover:bg-white/20'}`}
            >
              {getTierIcon(tier)}
              <span className="ml-1 capitalize">{tier === 'all' ? 'All' : tier}</span>
            </Button>
          ))}
        </div>
      </div>

      {/* Content */}
      <div className="p-4 pb-24 space-y-4">
        <Tabs defaultValue="top-makers" className="w-full">
          <TabsList className="grid w-full grid-cols-3 bg-white shadow-sm">
            <TabsTrigger value="top-makers">Top Makers</TabsTrigger>
            <TabsTrigger value="trending">Trending</TabsTrigger>
            <TabsTrigger value="following">Following</TabsTrigger>
          </TabsList>

          <TabsContent value="top-makers" className="space-y-4 mt-4">
            {isLoading ? (
              <div className="text-center py-12">
                <div className="w-8 h-8 mx-auto mb-4 border-4 border-pink-300 border-t-pink-600 rounded-full animate-spin"></div>
                <p className="text-gray-500">Loading taste makers...</p>
              </div>
            ) : filteredTasteMakers.length === 0 ? (
              <div className="text-center py-16">
                <Crown className="w-16 h-16 mx-auto text-gray-300 mb-4" />
                <h3 className="text-lg font-semibold text-gray-800 mb-2">No taste makers found</h3>
                <p className="text-gray-500 mb-4">Be the first to discover trending spots and become a taste maker!</p>
                <Button className="bg-gradient-to-r from-pink-500 to-purple-500">
                  Start Exploring
                </Button>
              </div>
            ) : (
              filteredTasteMakers.map((tasteMaker, index) => (
              <Card key={tasteMaker.id} className="overflow-hidden shadow-lg border-0 bg-white/80 backdrop-blur-sm">
                <CardContent className="p-0">
                  {/* Rank Badge */}
                  <div className="absolute top-4 left-4 z-10">
                    <div className={`flex items-center space-x-1 px-2 py-1 rounded-full text-xs font-bold ${getTierColor(tasteMaker.tier)}`}>
                      {getTierIcon(tasteMaker.tier)}
                      <span>#{index + 1}</span>
                    </div>
                  </div>

                  {/* Profile Header */}
                  <div className="relative bg-gradient-to-br from-pink-100 to-purple-100 p-6 pt-12">
                    <div className="flex items-start justify-between">
                      <div className="flex items-center space-x-4">
                        <div className="relative">
                          <Avatar className="w-16 h-16 border-4 border-white shadow-lg">
                            <AvatarImage src={tasteMaker.user.profilePictureUrl} alt={tasteMaker.user.username} />
                            <AvatarFallback className="bg-gradient-to-br from-pink-400 to-purple-400 text-white font-bold">
                              {tasteMaker.user.username.split(' ').map(n => n[0]).join('')}
                            </AvatarFallback>
                          </Avatar>
                          {tasteMaker.verificationStatus === 'featured' && (
                            <div className="absolute -top-1 -right-1 bg-yellow-400 rounded-full p-1">
                              <Crown className="w-3 h-3 text-yellow-800" />
                            </div>
                          )}
                          {tasteMaker.verificationStatus === 'verified' && (
                            <div className="absolute -top-1 -right-1 bg-blue-500 rounded-full p-1">
                              <CheckCircle className="w-3 h-3 text-white" />
                            </div>
                          )}
                        </div>

                        <div className="flex-1">
                          <h3 className="font-bold text-lg text-gray-900 flex items-center space-x-2">
                            <span>{tasteMaker.user.username}</span>
                          </h3>
                          <div className="flex items-center space-x-2 text-sm text-gray-600 mb-2">
                            <MapPin className="w-3 h-3" />
                            <span>{tasteMaker.locationCity}, {tasteMaker.locationCountry}</span>
                          </div>
                          <div className="flex items-center space-x-4 text-xs text-gray-600">
                            <div className="flex items-center space-x-1">
                              <Users className="w-3 h-3" />
                              <span>{tasteMaker.totalFollowers?.toLocaleString()} followers</span>
                            </div>
                            <div className="flex items-center space-x-1">
                              <TrendingUp className="w-3 h-3" />
                              <span>{tasteMaker.influenceScore?.toLocaleString()} influence</span>
                            </div>
                          </div>
                        </div>
                      </div>

                      <Button
                        onClick={() => handleFollow(tasteMaker.id)}
                        size="sm"
                        variant={followingIds.has(tasteMaker.id) || tasteMaker.isFollowing ? "outline" : "default"}
                        className={`${
                          followingIds.has(tasteMaker.id) || tasteMaker.isFollowing
                            ? 'border-pink-300 text-pink-600 hover:bg-pink-50'
                            : 'bg-gradient-to-r from-pink-500 to-purple-500 text-white hover:from-pink-600 hover:to-purple-600'
                        }`}
                      >
                        {followingIds.has(tasteMaker.id) || tasteMaker.isFollowing ? 'Following' : 'Follow'}
                      </Button>
                    </div>

                    {/* Bio */}
                    <p className="text-sm text-gray-700 mt-3 leading-relaxed">
                      {tasteMaker.bio}
                    </p>

                    {/* Specialties */}
                    <div className="flex flex-wrap gap-2 mt-3">
                      {tasteMaker.specialties?.map((specialty, idx) => (
                        <Badge key={idx} variant="secondary" className="text-xs bg-white/60 text-purple-700">
                          {specialty}
                        </Badge>
                      ))}
                    </div>

                    {/* Social Links */}
                    <div className="flex items-center space-x-4 mt-4">
                      {tasteMaker.instagramHandle && (
                        <div className="flex items-center space-x-1 text-xs text-gray-600">
                          <Instagram className="w-3 h-3" />
                          <span>@{tasteMaker.instagramHandle}</span>
                        </div>
                      )}
                      {tasteMaker.tiktokHandle && (
                        <div className="flex items-center space-x-1 text-xs text-gray-600">
                          <span className="font-bold">TT</span>
                          <span>@{tasteMaker.tiktokHandle}</span>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Stats Grid */}
                  <div className="grid grid-cols-3 gap-1 bg-white">
                    <div className="text-center p-3 border-r border-gray-100">
                      <div className="text-lg font-bold text-purple-600">{tasteMaker.spotsDiscovered}</div>
                      <div className="text-xs text-gray-600">Spots Found</div>
                    </div>
                    <div className="text-center p-3 border-r border-gray-100">
                      <div className="text-lg font-bold text-pink-600">{tasteMaker.trendsStarted}</div>
                      <div className="text-xs text-gray-600">Trends Started</div>
                    </div>
                    <div className="text-center p-3">
                      <div className="text-lg font-bold text-indigo-600">{Math.round((tasteMaker.influenceScore || 0) / 1000)}K</div>
                      <div className="text-xs text-gray-600">Influence</div>
                    </div>
                  </div>

                  {/* Recent Endorsement Preview */}
                  {tasteMaker.recentEndorsements && tasteMaker.recentEndorsements.length > 0 && (
                    <div className="bg-gradient-to-r from-purple-50 to-pink-50 p-4 border-t border-gray-100">
                      <div className="text-xs text-gray-600 mb-2">Latest Endorsement:</div>
                      <div className="flex items-start space-x-3">
                        <div className="w-12 h-12 bg-gradient-to-br from-pink-400 to-purple-400 rounded-lg flex items-center justify-center">
                          <MapPin className="w-6 h-6 text-white" />
                        </div>
                        <div className="flex-1">
                          <div className="font-medium text-sm text-gray-900">{tasteMaker.recentEndorsements[0].spot.name}</div>
                          <div className="text-xs text-gray-600 mt-1 line-clamp-2">{tasteMaker.recentEndorsements[0].caption}</div>
                          <div className="flex items-center space-x-3 mt-2">
                            <div className="flex items-center space-x-1 text-xs text-gray-500">
                              <Heart className="w-3 h-3" />
                              <span>{tasteMaker.recentEndorsements[0].engagement}</span>
                            </div>
                            <div className="text-xs text-gray-500">2h ago</div>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </CardContent>
              </Card>
              ))
            )}
          </TabsContent>

          <TabsContent value="trending" className="space-y-4 mt-4">
            <Card className="bg-white/80 backdrop-blur-sm shadow-lg border-0">
              <CardContent className="p-6 text-center">
                <TrendingUp className="w-12 h-12 text-purple-400 mx-auto mb-3" />
                <h3 className="font-bold text-lg text-gray-900 mb-2">Trending Endorsements</h3>
                <p className="text-sm text-gray-600 mb-4">
                  See what taste makers are recommending right now
                </p>
                <p className="text-sm text-gray-400">Available when users create endorsements</p>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="following" className="space-y-4 mt-4">
            <Card className="bg-white/80 backdrop-blur-sm shadow-lg border-0">
              <CardContent className="p-6 text-center">
                <Users className="w-12 h-12 text-purple-400 mx-auto mb-3" />
                <h3 className="font-bold text-lg text-gray-900 mb-2">Your Followed Taste Makers</h3>
                <p className="text-sm text-gray-600 mb-4">
                  Follow taste makers to get personalized spot recommendations
                </p>
                <p className="text-sm text-gray-400">Start following taste makers to see them here</p>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>

      <BottomNavigation />
    </div>
  );
}

